import React, { useState, useEffect } from "react";

import { NavLink, useHistory } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { CHECKOUT_UPDATE_PAGE } from "../../redux/actions";
import Checkout from "../shared/img/Checkout_button.png";
import Back from "../shared/img/Back_button.png";
const CheckoutPage = () => {
  const history = useHistory();
  let subtotal = 0;
  //on change set the value of all fields
  const formatter = new Intl.NumberFormat("en-US", {
    minimumFractionDigits: 2,
    maximumFractionDigits: 2,
  });
  const dispatch = useDispatch();
  const cartItems = useSelector((state) => state.cartReducers);
  const cartOrderState = useSelector((state) => state.orderCartReducers);

  const [billingFirstName, setBillingFirstName] = useState(
    cartOrderState.billingFirstName
  );
  const [billingLastName, setBillingLastName] = useState(
    cartOrderState.billingLastName
  );
  const [billingAddress1, setBillingAddress1] = useState(
    cartOrderState.billingAddress1
  );
  const [billingAddress2, setBillingAddress2] = useState(
    cartOrderState.billingAddress2
  );
  const [billingCity, setBillingCity] = useState(cartOrderState.billingCity);
  const [billingState, setBillingState] = useState(cartOrderState.billingState);
  const [billingPostalCode, setBillingPostalCode] = useState(
    cartOrderState.billingPostalCode
  );
  const [billingPhone, setBillingPhone] = useState(cartOrderState.billingPhone);
  const [billingEmail, setBillingEmail] = useState(cartOrderState.billingEmail);

  const [shippingFirstName, setShippingFirstName] = useState(
    cartOrderState.shippingFirstName
  );
  const [shippingLastName, setShippingLastName] = useState(
    cartOrderState.shippingLastName
  );
  const [shippingAddress1, setShippingAddress1] = useState(
    cartOrderState.shippingAddress1
  );
  const [shippingAddress2, setShippingAddress2] = useState(
    cartOrderState.shippingAddress2
  );
  const [shippingCity, setShippingCity] = useState(cartOrderState.shippingCity);
  const [shippingState, setShippingState] = useState(
    cartOrderState.shippingState
  );
  const [shippingPostalCode, setShippingPostalCode] = useState(
    cartOrderState.shippingPostalCode
  );
  const [shippingPhone, setShippingPhone] = useState(
    cartOrderState.shippingPhone
  );
  const [shippingEmail, setShippingEmail] = useState(
    cartOrderState.shippingEmail
  );
  const [note, setNote] = useState(cartOrderState.note);

  const handleUpdateBillFirstName = (billingFirstNameInput) => {
    setBillingFirstName(billingFirstNameInput.target.value);
  };
  const handleUpdateBillLastName = (billingLastNameInput) => {
    setBillingLastName(billingLastNameInput.target.value);
  };
  const handleUpdateBillAddress1 = (billingAddress1Input) => {
    setBillingAddress1(billingAddress1Input.target.value);
  };
  const handleUpdateBillAddress2 = (billingAddress2Input) => {
    setBillingAddress2(billingAddress2Input.target.value);
  };
  const handleUpdateBillCity = (billingCityInput) => {
    setBillingCity(billingCityInput.target.value);
  };
  const handleUpdateBillState = (billingStateInput) => {
    setBillingState(billingStateInput.target.value);
  };
  const handleUpdateBillPostalCode = (billingPostalCodeInput) => {
    setBillingPostalCode(billingPostalCodeInput.target.value);
  };
  const handleUpdateBillPhone = (billingPhoneInput) => {
    setBillingPhone(billingPhoneInput.target.value);
  };
  const handleUpdateBillEmail = (billingEmailInput) => {
    setBillingEmail(billingEmailInput.target.value);
  };

  /* - */

  const handleUpdateShipFirstName = (shippingFirstNameInput) => {
    setShippingFirstName(shippingFirstNameInput.target.value);
  };
  const handleUpdateShipLastName = (shippingLastNameInput) => {
    setShippingLastName(shippingLastNameInput.target.value);
  };
  const handleUpdateShipAddress1 = (shippingAddress1Input) => {
    setShippingAddress1(shippingAddress1Input.target.value);
  };
  const handleUpdateShipAddress2 = (shippingAddress2Input) => {
    setShippingAddress2(shippingAddress2Input.target.value);
  };
  const handleUpdateShipCity = (shippingCityInput) => {
    setShippingCity(shippingCityInput.target.value);
  };
  const handleUpdateShipState = (shippingStateInput) => {
    setShippingState(shippingStateInput.target.value);
  };
  const handleUpdateShipPostalCode = (shippingPostalCodeInput) => {
    setShippingPostalCode(shippingPostalCodeInput.target.value);
  };
  const handleUpdateShipPhone = (shippingPhoneInput) => {
    setShippingPhone(shippingPhoneInput.target.value);
  };
  const handleUpdateShipEmail = (shippingEmailInput) => {
    setShippingEmail(shippingEmailInput.target.value);
  };

  const handleUpdateNote = (noteInput) => {
    setNote(noteInput.target.value);
  };

  const checkoutClicked = () => {
    // Check if all required fields were enterd, if they were, store all values in the orderCart selectores state object
    if (
      billingFirstName == "" ||
      billingLastName == "" ||
      billingAddress1 == "" ||
      billingAddress2 == "" ||
      billingCity == "" ||
      billingState == "" ||
      billingPostalCode == "" ||
      billingPhone == "" ||
      billingEmail == "" ||
      shippingFirstName == "" ||
      shippingLastName == "" ||
      shippingAddress1 == "" ||
      shippingAddress2 == "" ||
      shippingCity == "" ||
      shippingState == "" ||
      shippingPostalCode == "" ||
      shippingPhone == "" ||
      shippingEmail == ""
    ) {
      console.log("Not Everythong Was Filled Out");
    } else {
      console.log("All Info Evaluated Was Valid");

      dispatch(
        CHECKOUT_UPDATE_PAGE(
          billingFirstName,
          billingLastName,
          billingAddress1,
          billingAddress2,
          billingCity,
          billingState,
          billingPostalCode,
          billingPhone,
          billingEmail,
          shippingFirstName,
          shippingLastName,
          shippingAddress1,
          shippingAddress2,
          shippingCity,
          shippingState,
          shippingPostalCode,
          shippingPhone,
          shippingEmail,
          note
        )
      );
      //Take the state and do an api call and update with http://localhost:5000/api/cart/checkout/update

      const fetchData = async () => {
        const result = await axios.patch(
          "http://localhost:5000/api/cart/checkout/update",
          {
            billingFirstName: billingFirstName,
            billingLastName: billingLastName,
            billingAddress1: billingAddress1,
            billingAddress2: billingAddress2,
            billingCity: billingCity,
            billingState: billingState,
            billingPostalCode: billingPostalCode,
            billingPhone: billingPhone,
            billingEmail: billingEmail,
            shippingFirstName,
            shippingLastName,
            shippingAddress1,
            shippingAddress2,
            shippingCity,
            shippingState,
            shippingPostalCode,
            shippingPhone,
            shippingEmail,
            note,
          }
        );

        let Cart = result.data;
        dispatch(CART_LOAD_PAGE(Cart));

        /*  console.log("line 28: ", result.data); */
      };
      fetchData();

      history.push("/cart/stepthree");
    }
  };
  const sameAsBillingClicked = () => {
    setShippingFirstName(billingFirstName);
    setShippingLastName(billingLastName);
    setShippingAddress1(billingAddress1);
    setShippingAddress2(billingAddress2);
    setShippingCity(billingCity);
    setShippingState(billingState);
    setShippingPostalCode(billingPostalCode);
    setShippingPhone(billingPhone);
    setShippingEmail(billingEmail);
  };

  return (
    <main class=" mt-3">
      <section class="display">
        <form
          action="/Cart/StepTwo/CustomerInfoUpdate"
          method="POST"
          class="center"
        >
          <div class="row container brownCartBackground cartText">
            <p>Enter Billing & Shipping Information Step 2 of 4</p>
          </div>
          <div class="row container lightbrownCartBackground  cartText">
            <div class="col-6 pl-3">
              <div class="brownCartBackground pl-3">Billing Address</div>
              <table
                cellpadding="5"
                cellspacing="5"
                width="100%"
                class="formBorder"
              >
                <tbody>
                  <tr class="mb-5">
                    <td class="pushToRight formPadding">First Name:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="billingFirstNameInput"
                        id="billingFirstNameInput"
                        required
                        value={billingFirstName}
                        onChange={(e) => {
                          handleUpdateBillFirstName(e);
                        }}
                      />
                    </td>
                  </tr>
                  <tr>
                    <td class="pushToRight formPadding">Last Name:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="billingLastNameInput"
                        id="billingLastNameInput"
                        value={billingLastName}
                        placeholder={cartOrderState.billingLastName}
                        onChange={(e) => {
                          handleUpdateBillLastName(e);
                        }}
                        required
                      />
                    </td>
                  </tr>

                  <tr>
                    <td class="pushToRight formPadding">Address:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="billingAddress1Input"
                        id="billingAddress1Input"
                        required
                        value={billingAddress1}
                        onChange={(e) => {
                          handleUpdateBillAddress1(e);
                        }}
                      />
                    </td>
                  </tr>
                  <tr>
                    <td class="pushToRight formPadding">Address 2:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="billingAddress2Input"
                        id="billingAddress2Input"
                        value={billingAddress2}
                        onChange={(e) => {
                          handleUpdateBillAddress2(e);
                        }}
                        placeholder="Optional"
                      />
                    </td>
                  </tr>

                  <tr>
                    <td class="pushToRight formPadding">City:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="billingCityInput"
                        id="billingCityInput"
                        value={billingCity}
                        onChange={(e) => {
                          handleUpdateBillCity(e);
                        }}
                        required
                      />
                    </td>
                  </tr>
                  <tr>
                    <td class="pushToRight formPadding">State:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="billingStateInput"
                        id="billingStateInput"
                        value={billingState}
                        onChange={(e) => {
                          handleUpdateBillState(e);
                        }}
                        required
                      />
                    </td>
                  </tr>

                  <tr>
                    <td class="pushToRight formPadding">Zip / Postal:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="billingZipInput"
                        id="billingZipInput"
                        value={billingPostalCode}
                        onChange={(e) => {
                          handleUpdateBillPostalCode(e);
                        }}
                        required
                      />
                    </td>
                  </tr>
                  <tr>
                    <td class="pushToRight formPadding">Phone:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="billingPhoneInput"
                        id="billingPhoneInput"
                        value={billingPhone}
                        onChange={(e) => {
                          handleUpdateBillPhone(e);
                        }}
                        required
                      />
                    </td>
                  </tr>
                  <tr>
                    <td class="pushToRight formPadding">Email:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="billingEmailInput"
                        id="billingEmailInput"
                        value={billingEmail}
                        onChange={(e) => {
                          handleUpdateBillEmail(e);
                        }}
                        required
                      />
                    </td>
                  </tr>
                </tbody>
              </table>
            </div>

            <div class="col-6 pl-3">
              <div class="brownCartBackground pl-3">
                Same as Billing Address{" "}
                <input
                  type="checkbox"
                  onChange={(e) => {
                    sameAsBillingClicked(e);
                  }}
                />
                Shipping
              </div>
              <table
                cellpadding="5"
                cellspacing="5"
                width="100%"
                class="formBorder"
              >
                <tbody>
                  <tr class="mb-5">
                    <td class="pushToRight formPadding">First Name:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="firstNameInput"
                        id="firstNameInput"
                        value={shippingFirstName}
                        onChange={(e) => {
                          handleUpdateShipFirstName(e);
                        }}
                        required
                      />
                    </td>
                  </tr>
                  <tr>
                    <td class="pushToRight formPadding">Last Name:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="lastNameInput"
                        id="lastNameInput"
                        value={shippingLastName}
                        onChange={(e) => {
                          handleUpdateShipLastName(e);
                        }}
                        required
                      />
                    </td>
                  </tr>

                  <tr>
                    <td class="pushToRight formPadding">Address:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="address1Input"
                        id="address1Input"
                        value={shippingAddress1}
                        onChange={(e) => {
                          handleUpdateShipAddress1(e);
                        }}
                        required
                      />
                    </td>
                  </tr>
                  <tr>
                    <td class="pushToRight formPadding">Address 2:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="address2Input"
                        id="address2Input"
                        value={shippingAddress2}
                        onChange={(e) => {
                          handleUpdateShipAddress2(e);
                        }}
                        placeholder="Optional"
                      />
                    </td>
                  </tr>

                  <tr>
                    <td class="pushToRight formPadding">City:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="cityInput"
                        id="cityInput"
                        value={shippingCity}
                        onChange={(e) => {
                          handleUpdateShipCity(e);
                        }}
                        required
                      />
                    </td>
                  </tr>
                  <tr>
                    <td class="pushToRight formPadding">State:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="stateInput"
                        id="stateInput"
                        value={shippingState}
                        onChange={(e) => {
                          handleUpdateShipState(e);
                        }}
                        required
                      />
                    </td>
                  </tr>

                  <tr>
                    <td class="pushToRight formPadding">Zip / Postal:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="zipInput"
                        id="zipInput"
                        value={shippingPostalCode}
                        onChange={(e) => {
                          handleUpdateShipPostalCode(e);
                        }}
                        required
                      />
                    </td>
                  </tr>
                  <tr>
                    <td class="pushToRight formPadding">Phone:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="phoneInput"
                        id="phoneInput"
                        value={shippingPhone}
                        onChange={(e) => {
                          handleUpdateShipPhone(e);
                        }}
                        required
                      />
                    </td>
                  </tr>
                  <tr>
                    <td class="pushToRight formPadding">Email:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="emailInput"
                        id="emailInput"
                        value={shippingEmail}
                        onChange={(e) => {
                          handleUpdateShipEmail(e);
                        }}
                        required
                      />
                    </td>
                  </tr>
                </tbody>
              </table>
            </div>
          </div>
          <div class="row container lightbrownCartBackground cartText mt-4 ">
            <table border="0" cellpadding="0" cellspacing="0" width="100%">
              <tbody>
                <tr>
                  <td
                    style={{
                      width: "500px",
                      height: "25px",
                      padding: "5px",
                      backgroundColor: "#d2be9d",
                      color: "#4b290c",
                      fontWeight: "bold",
                      borderTop: "1px dashed #929292",
                      borderLeft: "1px dashed #929292",
                    }}
                  >
                    Shipping to You
                  </td>
                  <td
                    style={{
                      width: "500px",
                      height: "25px",
                      padding: "5px",
                      backgroundColor: "#d2be9d",
                      color: "#4b290c",
                      fontWeight: "bold",
                      borderTop: "1px dashed #929292",
                      borderLeft: "1px dashed #929292",
                    }}
                  >
                    Price
                  </td>
                  <td
                    style={{
                      width: "500px",
                      height: "25px",
                      padding: "5px",
                      backgroundColor: "#d2be9d",
                      color: "#4b290c",
                      fontWeight: "bold",
                      borderTop: "1px dashed #929292",
                      borderLeft: "1px dashed #929292",
                    }}
                  >
                    Quantity
                  </td>
                  <td
                    style={{
                      width: "100px",
                      height: "25px",
                      textAlign: "center",
                      backgroundColor: "#d2be9d",
                      color: "#4b290c",
                      fontWeight: "bold",
                      borderTop: "1px dashed #929292",
                      borderLeft: "1px dashed #929292",
                      borderRight: "1px dashed #929292",
                    }}
                  >
                    Total
                  </td>
                </tr>
                {cartItems.map((item) => (
                  <tr
                    ng-repeat="product in ctrl.shopSvc.orderCart.products"
                    class="ng-scope"
                  >
                    <td
                      style={{
                        padding: "5px",
                        color: "#4b290c",
                        borderTop: "1px dashed #929292",
                        borderLeft: "1px dashed #929292",
                        textAlign: "center",
                      }}
                      class="ng-binding"
                    >
                      {item.name}
                    </td>
                    <td
                      style={{
                        padding: "5px",
                        color: "#4b290c",
                        borderTop: "1px dashed #929292",
                        borderLeft: "1px dashed #929292",
                        textAlign: "center",
                      }}
                      class="ng-binding"
                    >
                      ${formatter.format(item.price)}
                    </td>
                    <td
                      style={{
                        padding: "5px",
                        color: "#4b290c",
                        borderTop: "1px dashed #929292",
                        borderLeft: "1px dashed #929292",
                        textAlign: "center",
                      }}
                    >
                      {item.qty}
                    </td>

                    <td
                      style={{
                        borderTop: "1px dashed #929292",
                        borderLeft: "1px dashed #929292",
                        color: "#4b290c",
                        border: "1px solid #929292; text-align:center",
                        textAlign: "center",
                        borderRight: "1px dashed #929292",
                      }}
                    >
                      <div
                        style={{
                          paddingTop: "10px",
                        }}
                        class="ng-binding"
                      >
                        ${formatter.format(item.total)}
                        <div style={{ visibility: "hidden" }}>
                          {(subtotal += item.qty * item.price)}
                        </div>
                      </div>
                    </td>
                  </tr>
                ))}

                <tr
                  style={{
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "right",
                    borderTop: "1px dashed #929292",
                  }}
                >
                  <td
                    style={{
                      textAlign: "right",
                      color: "#4b290c",
                      fontWeight: "bold",
                    }}
                  ></td>
                  <td
                    style={{
                      paddingTop: "5px",
                      height: "35px",
                      color: "#4b290c",
                      fontWeight: "bold",
                      textAlign: "center",
                    }}
                  >
                    &nbsp;
                  </td>
                  <td
                    style={{
                      height: "35px",
                      color: "#4b290c",
                      fontWeight: "bold",
                      textAlign: "right",
                    }}
                  >
                    Subtotal:
                  </td>
                  <td
                    style={{
                      height: "35px",
                      color: "#4b290c",
                      fontWeight: "bold",
                      textAlign: "center",
                    }}
                    class="ng-binding"
                  >
                    ${formatter.format(subtotal)}
                  </td>
                </tr>
              </tbody>
            </table>
          </div>

          <div class="row container lightbrownCartBackground  cartText formBorder pushToLeft">
            <div>
              <p>Comments or Additional Information</p>
            </div>
            <div class=" container lightbrownCartBackground  cartText">
              <p>
                <textarea
                  rows="4"
                  cols="50"
                  name="noteInput"
                  id="noteInput"
                  value={note}
                  onChange={(e) => {
                    handleUpdateNote(e);
                  }}
                />
              </p>
            </div>
          </div>

          <div class="row container lightbrownCartBackground  cartText">
            <div class="col-8 center"></div>

            <div class="col-4 center ">
              <NavLink to="/Cart">
                <img src={Back} class="pr-3" alt="" />
              </NavLink>
              {/* This could be a button that triggers a functon that checks to see if any of our state thingys are empty and if they are all filled we can set the redux state to that */}

              <img
                src={Checkout}
                type="button"
                class="pr-3"
                onClick={(e) => checkoutClicked(e)}
              />
            </div>
          </div>
        </form>
      </section>
      {/* <!-- -------------------------------------------------------------------------------------------------------------------------------- --> */}
      <section class="display2 center">
        <div class="col-12 center  mt-4" style={{ padding: "10px" }}>
          <form
            action="/Cart/StepTwo/CustomerInfoUpdate"
            method="POST"
            style={{ width: "100%" }}
          >
            <div
              class="col lightbrownCartBackground mt-3"
              style={{ color: "black" }}
            >
              <div class=" brownCartBackground  cartText">
                <p>Payment Information Step 3 of 4</p>
              </div>

              <div class="col-12">
                <div class="center brownCartBackground">
                  <p>Billing Address</p>
                </div>
              </div>

              <div class="col-12">
                <table
                  cellpadding="5"
                  cellspacing="5"
                  width="100%"
                  class="formBorder"
                >
                  <tbody>
                    <tr class="mb-5">
                      <td class="pushToRight formPadding">First Name:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="firstNameInput"
                          id="botFirstNameInput"
                          value={billingFirstName}
                          onChange={(e) => {
                            handleUpdateBillFirstName(e);
                          }}
                          required
                          style={{ width: "90%" }}
                        />
                      </td>
                    </tr>
                    <tr>
                      <td class="pushToRight formPadding">Last Name:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="lastNameInput"
                          id="botLastNameInput"
                          style={{ width: "90%" }}
                          value={billingLastName}
                          onChange={(e) => {
                            handleUpdateBillLastName(e);
                          }}
                          required
                        />
                      </td>
                    </tr>

                    <tr>
                      <td class="pushToRight formPadding">Address:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="address1Input"
                          id="botAddress1Input"
                          style={{ width: "90%" }}
                          value={billingAddress1}
                          onChange={(e) => {
                            handleUpdateBillAddress1(e);
                          }}
                          required
                        />
                      </td>
                    </tr>
                    <tr>
                      <td class="pushToRight formPadding">Address 2:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="address2Input"
                          id="botAddress2Input"
                          value={billingAddress2}
                          onChange={(e) => {
                            handleUpdateBillAddress2(e);
                          }}
                          style={{ width: "90%" }}
                          placeholder="Optional"
                        />
                      </td>
                    </tr>

                    <tr>
                      <td class="pushToRight formPadding">City:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          style={{ width: "90%" }}
                          name="cityInput"
                          id="botCityInput"
                          value={billingCity}
                          onChange={(e) => {
                            handleUpdateBillCity(e);
                          }}
                          required
                        />
                      </td>
                    </tr>
                    <tr>
                      <td class="pushToRight formPadding">State:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="stateInput"
                          id="botStateInput"
                          style={{ width: "90%" }}
                          value={billingState}
                          onChange={(e) => {
                            handleUpdateBillState(e);
                          }}
                          required
                        />
                      </td>
                    </tr>

                    <tr>
                      <td class="pushToRight formPadding">Zip / Postal:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="zipInput"
                          id="botZipInput"
                          value={billingPostalCode}
                          onChange={(e) => {
                            handleUpdateBillPostalCode(e);
                          }}
                          style={{ width: "90%" }}
                          required
                        />
                      </td>
                    </tr>
                    <tr>
                      <td class="pushToRight formPadding">Phone:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="phoneInput"
                          id="botPhoneInput"
                          value={billingPhone}
                          onChange={(e) => {
                            handleUpdateBillPhone(e);
                          }}
                          style={{ width: "90%" }}
                          required
                        />
                      </td>
                    </tr>
                    <tr>
                      <td class="pushToRight formPadding">Email:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="emailInput"
                          id="botEmailInput"
                          style={{ width: "90%" }}
                          value={billingEmail}
                          onChange={(e) => {
                            handleUpdateBillEmail(e);
                          }}
                          required
                        />
                      </td>
                    </tr>
                  </tbody>
                </table>
              </div>

              <div class="col-12">
                <div class="brownCartBackground">
                  Same as shipping.
                  <input
                    type="checkbox"
                    onChange={(e) => {
                      sameAsBillingClicked(e);
                    }}
                  />{" "}
                  Billing
                </div>
                <table
                  cellpadding="5"
                  cellspacing="5"
                  width="100%"
                  class="formBorder"
                >
                  <tbody>
                    <tr class="mb-5">
                      <td class="pushToRight formPadding">First Name:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="billingFirstNameInput"
                          style={{ width: "90%" }}
                          id="botBillingFirstNameInput"
                          value={shippingFirstName}
                          onChange={(e) => {
                            handleUpdateShipFirstName(e);
                          }}
                          required
                        />
                      </td>
                    </tr>
                    <tr>
                      <td class="pushToRight formPadding">Last Name:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="billingLastNameInput"
                          style={{ width: "90%" }}
                          value={shippingLastName}
                          onChange={(e) => {
                            handleUpdateShipLastName(e);
                          }}
                          id="botBillingLastNameInput"
                          required
                        />
                      </td>
                    </tr>

                    <tr>
                      <td class="pushToRight formPadding">Address:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="billingAddress1Input"
                          style={{ width: "90%" }}
                          value={shippingAddress1}
                          onChange={(e) => {
                            handleUpdateShipAddress1(e);
                          }}
                          required
                        />
                      </td>
                    </tr>
                    <tr>
                      <td class="pushToRight formPadding">Address 2:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="billingAddress2Input"
                          style={{ width: "90%" }}
                          value={shippingAddress2}
                          onChange={(e) => {
                            handleUpdateShipAddress2(e);
                          }}
                          id="botBillingAddress2Input"
                          placeholder="Optional"
                        />
                      </td>
                    </tr>

                    <tr>
                      <td class="pushToRight formPadding">City:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="billingCityInput"
                          style={{ width: "90%" }}
                          value={shippingCity}
                          onChange={(e) => {
                            handleUpdateShipCity(e);
                          }}
                          id="botBillingCityInput"
                          required
                        />
                      </td>
                    </tr>
                    <tr>
                      <td class="pushToRight formPadding">State:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="billingStateInput"
                          style={{ width: "90%" }}
                          id="botBillingStateInput"
                          value={shippingState}
                          onChange={(e) => {
                            handleUpdateShipState(e);
                          }}
                          required
                        />
                      </td>
                    </tr>

                    <tr>
                      <td class="pushToRight formPadding">Zip / Postal:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="billingZipInput"
                          style={{ width: "90%" }}
                          id="botBillingZipInput"
                          value={shippingPostalCode}
                          onChange={(e) => {
                            handleUpdateShipPostalCode(e);
                          }}
                          required
                        />
                      </td>
                    </tr>
                    <tr>
                      <td class="pushToRight formPadding">Phone:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="billingPhoneInput"
                          style={{ width: "90%" }}
                          id="botBillingPhoneInput"
                          value={shippingPhone}
                          onChange={(e) => {
                            handleUpdateShipPhone(e);
                          }}
                          required
                        />
                      </td>
                    </tr>
                    <tr>
                      <td class="pushToRight formPadding">Email:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="billingEmailInput"
                          style={{ width: "90%" }}
                          value={shippingEmail}
                          onChange={(e) => {
                            handleUpdateShipEmail(e);
                          }}
                          id="botBillingEmailInput"
                          required
                        />
                      </td>
                    </tr>
                  </tbody>
                </table>
              </div>
            </div>
            <div class="col-12 lightbrownCartBackground cartText mt-4">
              <table border="0" cellpadding="0" cellspacing="0" width="100%">
                <tbody>
                  <tr>
                    <td
                      style={{
                        width: "500px",
                        height: "25px",
                        padding: "5px",
                        backgroundColor: "#d2be9d",
                        color: "#4b290c",
                        fontWeight: "bold",
                        borderTop: "1px dashed #929292",
                        borderLeft: "1px dashed #929292",
                      }}
                    >
                      Shipping to You
                    </td>
                    <td
                      style={{
                        width: "500px",
                        height: "25px",
                        padding: "5px",
                        backgroundColor: "#d2be9d",
                        color: "#4b290c",
                        fontWeight: "bold",
                        borderTop: "1px dashed #929292",
                        borderLeft: "1px dashed #929292",
                      }}
                    >
                      Price
                    </td>
                    <td
                      style={{
                        width: "500px",
                        height: "25px",
                        padding: "5px",
                        backgroundColor: "#d2be9d",
                        color: "#4b290c",
                        fontWeight: "bold",
                        borderTop: "1px dashed #929292",
                        borderLeft: "1px dashed #929292",
                      }}
                    >
                      Quantity
                    </td>
                    <td
                      style={{
                        width: "100px",
                        height: "25px",
                        textAlign: "center",
                        backgroundColor: "#d2be9d",
                        color: "#4b290c",
                        fontWeight: "bold",
                        borderTop: "1px dashed #929292",
                        borderLeft: "1px dashed #929292",
                        borderRight: "1px dashed #929292",
                      }}
                    >
                      Total
                    </td>
                  </tr>
                  {cartItems.map((item) => (
                    <tr
                      ng-repeat="product in ctrl.shopSvc.orderCart.products"
                      class="ng-scope"
                    >
                      <td
                        style={{
                          padding: "5px",
                          color: "#4b290c",
                          borderTop: "1px dashed #929292",
                          borderLeft: "1px dashed #929292",
                          textAlign: "center",
                        }}
                        class="ng-binding"
                      >
                        {item.name}
                      </td>
                      <td
                        style={{
                          padding: "5px",
                          color: "#4b290c",
                          borderTop: "1px dashed #929292",
                          borderLeft: "1px dashed #929292",
                          textAlign: "center",
                        }}
                        class="ng-binding"
                      >
                        ${formatter.format(item.price)}
                      </td>
                      <td
                        style={{
                          padding: "5px",
                          color: "#4b290c",
                          borderTop: "1px dashed #929292",
                          borderLeft: "1px dashed #929292",
                          textAlign: "center",
                        }}
                      >
                        {item.qty}
                      </td>

                      <td
                        style={{
                          borderTop: "1px dashed #929292",
                          borderLeft: "1px dashed #929292",
                          color: "#4b290c",
                          border: "1px solid #929292; text-align:center",
                          textAlign: "center",
                          borderRight: "1px dashed #929292",
                        }}
                      >
                        <div
                          style={{
                            paddingTop: "10px",
                          }}
                          class="ng-binding"
                        >
                          ${formatter.format(item.total)}
                          <div style={{ visibility: "hidden" }}>
                            {(subtotal += item.qty * item.price)}
                          </div>
                        </div>
                      </td>
                    </tr>
                  ))}

                  <tr
                    style={{
                      height: "35px",
                      color: "#4b290c",
                      fontWeight: "bold",
                      textAlign: "right",
                      borderTop: "1px dashed #929292",
                    }}
                  >
                    <td
                      style={{
                        textAlign: "right",
                        color: "#4b290c",
                        fontWeight: "bold",
                      }}
                    ></td>
                    <td
                      style={{
                        paddingTop: "5px",
                        height: "35px",
                        color: "#4b290c",
                        fontWeight: "bold",
                        textAlign: "center",
                      }}
                    >
                      &nbsp;
                    </td>
                    <td
                      style={{
                        height: "35px",
                        color: "#4b290c",
                        fontWeight: "bold",
                        textAlign: "right",
                      }}
                    >
                      Subtotal:
                    </td>
                    <td
                      style={{
                        height: "35px",
                        color: "#4b290c",
                        fontWeight: "bold",
                        textAlign: "center",
                      }}
                      class="ng-binding"
                    >
                      ${formatter.format(subtotal)}
                    </td>
                  </tr>
                </tbody>
              </table>

              <div class="col-12 lightbrownCartBackground cartText formBorder pushToLeft">
                <div>
                  <p>Comments or Additional Information</p>
                </div>
                <div class="  lightbrownCartBackground cartText">
                  <p>
                    <textarea
                      rows="4"
                      cols="30"
                      name="noteInput"
                      id="noteInput"
                      value={note}
                      onChange={(e) => {
                        handleUpdateNote(e);
                      }}
                    >
                      Note
                    </textarea>
                  </p>
                </div>
              </div>

              <div class="col-12 lightbrownCartBackground cartText">
                <div class="pushToRight ">
                  <NavLink to="/Cart">
                    <img src={Back} class="" alt=""></img>
                  </NavLink>
                  <img
                    src={Checkout}
                    type="button"
                    class="pr-3"
                    onClick={(e) => checkoutClicked(e)}
                  />
                </div>
              </div>
            </div>
          </form>
        </div>
      </section>
    </main>
  );
};
export default CheckoutPage;
