                          style={{ width: "90%" }}
                          value={shippingEmail}
                          onChange={(e) => {
                            handleUpdateShipEmail(e);
                          }}
                          id="botBillingEmailInput"
                          required
                        />
                      </td>
                    </tr>
                  </tbody>
                </table>
              </div>
            </div>
            <div className="col-12 lightbrownCartBackground cartText ">
              <table border="0" cellpadding="0" cellspacing="0" width="100%">
                <tbody>
                  <tr>
                    <td
                      style={{
                        width: "500px",
                        height: "25px",
                        padding: "5px",
                        backgroundColor: "#d2be9d",
                        color: "#4b290c",
                        fontWeight: "bold",
                        borderTop: "1px dashed #929292",
                        borderLeft: "1px dashed #929292",
                      }}
                    >
                      Shipping to You
                    </td>
                    <td
                      style={{
                        width: "500px",
                        height: "25px",
                        padding: "5px",
                        backgroundColor: "#d2be9d",
                        color: "#4b290c",
                        fontWeight: "bold",
                        borderTop: "1px dashed #929292",
                        borderLeft: "1px dashed #929292",
                      }}
                    >
                      Price
                    </td>
                    <td
                      style={{
                        width: "500px",
                        height: "25px",
                        padding: "5px",
                        backgroundColor: "#d2be9d",
                        color: "#4b290c",
                        fontWeight: "bold",
                        borderTop: "1px dashed #929292",
                        borderLeft: "1px dashed #929292",
                      }}
                    >
                      Quantity
                    </td>
                    <td
                      style={{
                        width: "100px",
                        height: "25px",
                        textAlign: "center",
                        backgroundColor: "#d2be9d",
                        color: "#4b290c",
                        fontWeight: "bold",
                        borderTop: "1px dashed #929292",
                        borderLeft: "1px dashed #929292",
                        borderRight: "1px dashed #929292",
                      }}
                    >
                      Total
                    </td>
                  </tr>
                  {cartItems.map((item) => (
                    <tr
                      ng-repeat="product in ctrl.shopSvc.orderCart.products"
                      className="ng-scope"
                    >
                      <td
                        style={{
                          padding: "5px",
                          color: "#4b290c",
                          borderTop: "1px dashed #929292",
                          borderLeft: "1px dashed #929292",
                          textAlign: "center",
                        }}
                        className="ng-binding"
                      >
                        {item.name}
                      </td>
                      <td
                        style={{
                          padding: "5px",
                          color: "#4b290c",
                          borderTop: "1px dashed #929292",
                          borderLeft: "1px dashed #929292",
                          textAlign: "center",
                        }}
                        className="ng-binding"
                      >
                        ${formatter.format(item.price)}
                      </td>
                      <td
                        style={{
                          padding: "5px",
                          color: "#4b290c",
                          borderTop: "1px dashed #929292",
                          borderLeft: "1px dashed #929292",
                          textAlign: "center",
                        }}
                      >
                        {item.qty}
                      </td>

                      <td
                        style={{
                          borderTop: "1px dashed #929292",
                          borderLeft: "1px dashed #929292",
                          color: "#4b290c",
                          border: "1px solid #929292; text-align:center",
                          textAlign: "center",
                          borderRight: "1px dashed #929292",
                        }}
                      >
                        <div
                          style={{
                            paddingTop: "10px",
                          }}
                          className="ng-binding"
                        >
                          ${formatter.format(item.total)}
                          <div style={{ visibility: "hidden" }}>
                            {(subtotal += item.qty * item.price)}
                          </div>
                        </div>
                      </td>
                    </tr>
                  ))}

                  <tr
                    style={{
                      height: "35px",
                      color: "#4b290c",
                      fontWeight: "bold",
                      textAlign: "right",
                      borderTop: "1px dashed #929292",
                    }}
                  >
                    <td
                      style={{
                        textAlign: "right",
                        color: "#4b290c",
                        fontWeight: "bold",
                      }}
                    ></td>
                    <td
                      style={{
                        paddingTop: "5px",
                        height: "35px",
                        color: "#4b290c",
                        fontWeight: "bold",
                        textAlign: "center",
                      }}
                    >
                      &nbsp;
                    </td>
                    <td
                      style={{
                        height: "35px",
                        color: "#4b290c",
                        fontWeight: "bold",
                        textAlign: "right",
                      }}
                    >
                      Subtotal:
                    </td>
                    <td
                      style={{
                        height: "35px",
                        color: "#4b290c",
                        fontWeight: "bold",
                        textAlign: "center",
                      }}
                      className="ng-binding"
                    >
                      ${formatter.format(subtotal)}
                    </td>
                  </tr>
                </tbody>
              </table>

              <div className="col-12 lightbrownCartBackground cartText formBorder pushToLeft">
                <div>
                  <p>Comments or Additional Information</p>
                </div>
                <div className="  lightbrownCartBackground cartText">
                  <p>
                    <textarea
                      rows="4"
                      cols="30"
                      name="noteInput"
                      id="noteInput"
                      value={note}
                      onChange={(e) => {
                        handleUpdateNote(e);
                      }}
                    >
                      Note
                    </textarea>
                  </p>
                </div>
              </div>

              <div className="col-12 lightbrownCartBackground cartText">
                <div className="pushToRight ">
                  <NavLink to="/Cart">
                    <img src={Back} alt="cart"></img>
                  </NavLink>
                  <img
                    src={Checkout}
                    type="button"
                    className="pr-3"
                    alt="checkout"
                    onClick={(e) => checkoutClicked(e)}
                  />
                </div>
              </div>
            </div>
          </form>
        </div>
      </section>
    </main>
  );
};
export default CheckoutPage;
