import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { CART_LOAD_PAGE, CART_UPDATE_PAGE } from "../../redux/actions";
import axios from "axios";
import {} from "../../redux/actions";
import { NavLink } from "react-router-dom";
import ContinueShopping from "../shared/img/ContinueShopping_button.png";
import Checkout_button from "../shared/img/Checkout_button.png";
import Remove_Button from "../shared/img/Remove_Button.png";
const CartPage = () => {
  // You need to use redux from here out to track the state so changes can be reflected on the view
  const dispatch = useDispatch();
  const [qty, setQty] = useState(0);
  const handleSetQty = (qty) => {
    setQty(qty.target.value.split(/\D/).join(""));
  };
  let subtotal = 0;
  const cartItems = useSelector((state) => state.cartReducers);
  const sessionId = useSelector((state) => state.sessionReducers);
  let numOfItems = cartItems.length;
  const fetchData = async () => {
    const result = await axios.post("http://localhost:5000/api/cart/list", {
      sessionId: sessionId,
    });
    if (result.data.length > 0) {
      let Cart = result.data;
      dispatch(CART_LOAD_PAGE(Cart));
    }
    /*  console.log("line 28: ", result.data); */
  };

  const handleUpdateCart = (qty, orderItemId) => {
    dispatch(CART_UPDATE_PAGE(qty, orderItemId));
    fetchData();
    setQty(qty);
    const addToCartAxios = async () => {
      const res = await axios.patch("http://localhost:5000/api/cart/update", {
        orderItemIdInput: orderItemId,
        qtyInput: qty,
      });
    };
    addToCartAxios();
  };
  useEffect(() => {
    fetchData();
    /*     console.log(cartItems); */
  }, [cartItems]);
  const formatter = new Intl.NumberFormat("en-US", {
    minimumFractionDigits: 2,
    maximumFractionDigits: 2,
  });
  return (
    <main className="center">
      <section className="display center fullWidth">
        <div className="row container brownCartBackground cartText mt-5 mb-5">
          <div className="col-8 center">
            <p>
              SHOPPING CART: Your shopping cart contains {numOfItems} item(s)
              items.
            </p>
          </div>

          <div className="col-4 center">
            <NavLink to="/Shop">
              <img
                src={ContinueShopping}
                className="pr-3"
                alt="This is an img"
              ></img>
            </NavLink>
            {numOfItems == 0 ? (
              ""
            ) : (
              <NavLink to="/cart/checkout">
                <img src={Checkout_button} alt="This is an img" />
              </NavLink>
            )}
          </div>
        </div>
        <div className="row container lightbrownCartBackground cartText mt-4 ">
          <table border="0" cellpadding="0" cellspacing="0" width="100%">
            <tbody>
              <tr>
                <td
                  style={{
                    width: "500px",
                    height: "25px",
                    padding: "5px",
                    backgroundColor: "#d2be9d",
                    color: "#4b290c",
                    fontWeight: "bold",
                    borderTop: "1px dashed #929292",
                    borderLeft: "1px dashed #929292",
                    textAlign: "center",
                  }}
                >
                  Shipping to You
                </td>
                <td
                  style={{
                    width: "100px",
                    height: "25px",
                    padding: "5px",
                    backgroundColor: "#d2be9d",
                    color: "#4b290c",
                    fontWeight: "bold",
                    borderTop: "1px dashed #929292",
                    borderLeft: "1px dashed #929292",
                    textAlign: "center",
                  }}
                >
                  Price
                </td>
                <td
                  style={{
                    width: "100px",
                    height: "25px",
                    padding: "5px",
                    backgroundColor: "#d2be9d",
                    color: "#4b290c",
                    fontWeight: "bold",
                    borderTop: "1px dashed #929292",
                    borderLeft: "1px dashed #929292",
                    textAlign: "center",
                  }}
                >
                  Quantity
                </td>
                <td
                  style={{
                    width: "100px",
                    height: "25px",
                    padding: "5px",
                    backgroundColor: "#d2be9d",
                    color: "#4b290c",
                    fontWeight: "bold",
                    borderTop: "1px dashed #929292",
                    borderLeft: "1px dashed #929292",
                    textAlign: "center",
                  }}
                >
                  Total
                </td>
              </tr>
              {cartItems.map((item) => (
                <tr
                  ng-repeat="product in ctrl.shopSvc.orderCart.products"
                  class="ng-scope"
                >
                  <td
                    style={{
                      padding: "5px",
                      color: "#4b290c",
                      borderTop: "1px dashed #929292",
                      borderLeft: "1px dashed #929292",
                      textAlign: "center",
                    }}
                    class="ng-binding"
                  >
                    {item.name}
                  </td>
                  <td
                    style={{
                      padding: "5px",
                      color: "#4b290c",
                      borderTop: "1px dashed #929292",
                      borderLeft: "1px dashed #929292",
                      textAlign: "center",
                    }}
                    class="ng-binding"
                  >
                    ${formatter.format(item.price)}
                  </td>
                  <td
                    style={{
                      padding: "5px",
                      color: "#4b290c",
                      borderTop: "1px dashed #929292",
                      borderLeft: "1px dashed #929292",
                      textAlign: "center",
                    }}
                  >
                    <input
                      class="UpdateQty ng-pristine ng-untouched ng-valid ng-not-empty ng-valid-maxlength"
                      type="number"
                      pattern="[0-9]"
                      placeholder={item.qty}
                      name="qtyInput"
                      id="qtyInput"
                      onChange={(e) => {
                        handleSetQty(e);
                      }}
                      onBlur={() => handleUpdateCart(qty, item.orderItemId)}
                      ng-keyup="ctrl.updateQty(product);"
                      ng-keypress="ctrl.numbersRequired($event)"
                      maxlength="2"
                      style={{
                        width: "45px",
                        height: "25px",
                        padding: "5px",
                        color: "#4b290c",
                        border: "1px solid #929292; text-align:center",
                        textAlign: "center",
                      }}
                    />
                  </td>

                  <td
                    style={{
                      borderTop: "1px dashed #929292",
                      borderLeft: "1px dashed #929292",
                      color: "#4b290c",
                      border: "1px solid #929292; text-align:center",
                      textAlign: "center",
                      borderRight: "1px dashed #929292",
                    }}
                  >
                    <div
                      style={{
                        paddingTop: "10px",
                      }}
                      class="ng-binding"
                    >
                      ${formatter.format(item.total)}
                      <div style={{ visibility: "hidden" }}>
                        {(subtotal += item.qty * item.price)}
                      </div>
                    </div>

                    <div
                      style={{
                        paddingTop: "10px",
                        paddingBottom: "5px",
                      }}
                    >
                      <img
                        style={{
                          cursor: "pointer",
                        }}
                        ng-click="ctrl.removeCart(product);"
                        src={Remove_Button}
                      />
                    </div>
                  </td>
                </tr>
              ))}

              <tr>
                <td
                  style={{
                    borderTop: "1px dashed #929292",
                    borderLeft: "1px dashed #929292",
                    paddingTop: "10px",
                    paddingBottom: "5px",
                    textAlign: "right",
                    color: "#4b290c",
                    fontWeight: "bold",
                  }}
                ></td>
                <td
                  style={{
                    paddingTop: "5px",
                    paddingBottom: "5px",
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "right",
                    borderTop: "1px dashed #929292",
                  }}
                >
                  &nbsp;
                </td>

                <td
                  style={{
                    paddingTop: "5px",
                    paddingBottom: "5px",
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "right",
                    borderTop: "1px dashed #929292",
                  }}
                >
                  Subtotal:
                </td>
                <td
                  style={{
                    paddingTop: "5px",
                    paddingBottom: "5px",
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "center",
                    borderTop: "1px dashed #929292",
                    borderRight: "1px dashed #929292",
                  }}
                  class="ng-binding"
                >
                  ${formatter.format(subtotal)}
                </td>
              </tr>
              <tr>
                <td
                  colspan="4"
                  style={{
                    paddingRight: "5px",

                    height: "45px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "right",
                    border: "1px dashed #929292",
                  }}
                >
                  {numOfItems == 0 ? (
                    ""
                  ) : (
                    <NavLink to="/cart/checkout">
                      <img
                        alt="Check Out"
                        style={{
                          cursor: "pointer",
                          float: "right",
                          padding: "0 0 0 5px",
                          width: "100px",
                        }}
                        src={Checkout_button}
                        alt="This is an img"
                      ></img>
                    </NavLink>
                  )}

                  <NavLink
                    to="/shop"
                    style={{
                      float: "right",
                      padding: "0 5px 0 0",
                    }}
                  >
                    <img
                      alt="Continue Shopping"
                      ng-src="/app/shop/cart/images/ContinueShopping_button.png"
                      class="img-responsive"
                      src={ContinueShopping}
                    />
                  </NavLink>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      </section>

      <section className="display2 center">
        <div className="brownCartBackground cartText mt-5 mb-3 center fullWidth">
          <p>
            SHOPPING CART: Your shopping cart contains {numOfItems} item(s).
          </p>

          <div className="col-4 center">
            <NavLink to="/Shop">
              <img
                src={ContinueShopping}
                className="pb-1"
                alt="This is an img"
              />
            </NavLink>

            <NavLink to="/Cart/StepTwo">
              <img
                src={Checkout_button}
                className="pb-1"
                alt="This is an img"
              />
            </NavLink>
          </div>
        </div>
        <div className=" lightbrownCartBackground cartText fullWidth">
          <table border="0" cellpadding="0" cellspacing="0" width="100%">
            <tbody>
              <tr>
                <td
                  style={{
                    width: "500px",
                    height: "25px",
                    padding: "5px",
                    backgroundColor: "#d2be9d",
                    color: "#4b290c",
                    fontWeight: "bold",
                    borderTop: "1px dashed #929292",
                    borderLeft: "1px dashed #929292",
                    textAlign: "center",
                  }}
                >
                  Shipping to You
                </td>
                <td
                  style={{
                    width: "100px",
                    height: "25px",
                    padding: "5px",
                    backgroundColor: "#d2be9d",
                    color: "#4b290c",
                    fontWeight: "bold",
                    borderTop: "1px dashed #929292",
                    borderLeft: "1px dashed #929292",
                    textAlign: "center",
                  }}
                >
                  Price
                </td>
                <td
                  style={{
                    width: "100px",
                    height: "25px",
                    padding: "5px",
                    backgroundColor: "#d2be9d",
                    color: "#4b290c",
                    fontWeight: "bold",
                    borderTop: "1px dashed #929292",
                    borderLeft: "1px dashed #929292",
                    textAlign: "center",
                  }}
                >
                  Quantity
                </td>
                <td
                  style={{
                    width: "100px",
                    height: "25px",
                    padding: "5px",
                    backgroundColor: "#d2be9d",
                    color: "#4b290c",
                    fontWeight: "bold",
                    borderTop: "1px dashed #929292",
                    borderLeft: "1px dashed #929292",
                    textAlign: "center",
                  }}
                >
                  Total
                </td>
              </tr>
              {cartItems.map((item) => (
                <tr
                  ng-repeat="product in ctrl.shopSvc.orderCart.products"
                  class="ng-scope"
                >
                  <td
                    style={{
                      padding: "5px",
                      color: "#4b290c",
                      borderTop: "1px dashed #929292",
                      borderLeft: "1px dashed #929292",
                      textAlign: "center",
                    }}
                    class="ng-binding"
                  >
                    {item.name}
                  </td>
                  <td
                    style={{
                      padding: "5px",
                      color: "#4b290c",
                      borderTop: "1px dashed #929292",
                      borderLeft: "1px dashed #929292",
                      textAlign: "center",
                    }}
                    class="ng-binding"
                  >
                    ${formatter.format(item.price)}
                  </td>
                  <td
                    style={{
                      padding: "5px",
                      color: "#4b290c",
                      borderTop: "1px dashed #929292",
                      borderLeft: "1px dashed #929292",
                      textAlign: "center",
                    }}
                  >
                    <input
                      class="UpdateQty ng-pristine ng-untouched ng-valid ng-not-empty ng-valid-maxlength"
                      type="number"
                      pattern="[0-9]"
                      placeholder={item.qty}
                      name="qtyInput"
                      id="qtyInput"
                      onChange={(e) => {
                        handleSetQty(e);
                      }}
                      onBlur={() => handleUpdateCart(qty, item.orderItemId)}
                      style={{
                        width: "45px",
                        height: "25px",
                        padding: "5px",
                        color: "#4b290c",
                        border: "1px solid #929292; text-align:center",
                        textAlign: "center",
                      }}
                      ng-blur="ctrl.validateQty(product);"
                      ng-keyup="ctrl.updateQty(product);"
                      ng-keypress="ctrl.numbersRequired($event)"
                      maxlength="2"
                    />
                  </td>

                  <td
                    style={{
                      borderTop: "1px dashed #929292",
                      borderLeft: "1px dashed #929292",
                      color: "#4b290c",
                      border: "1px solid #929292; text-align:center",
                      textAlign: "center",
                      borderRight: "1px dashed #929292",
                    }}
                  >
                    <div
                      style={{
                        paddingTop: "10px",
                      }}
                      class="ng-binding"
                    >
                      ${formatter.format(item.qty * item.price)}
                      <div style={{ visibility: "hidden" }}></div>
                    </div>

                    <div
                      style={{
                        paddingTop: "10px",
                        paddingBottom: "5px",
                      }}
                    >
                      <img
                        style={{
                          cursor: "pointer",
                        }}
                        ng-click="ctrl.removeCart(product);"
                        src={Remove_Button}
                      />
                    </div>
                  </td>
                </tr>
              ))}

              <tr>
                <td
                  style={{
                    borderTop: "1px dashed #929292",
                    borderLeft: "1px dashed #929292",
                    paddingTop: "10px",
                    paddingBottom: "5px",
                    textAlign: "right",
                    color: "#4b290c",
                    fontWeight: "bold",
                  }}
                ></td>
                <td
                  style={{
                    paddingTop: "5px",
                    paddingBottom: "5px",
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "right",
                    borderTop: "1px dashed #929292",
                  }}
                >
                  &nbsp;
                </td>

                <td
                  style={{
                    paddingTop: "5px",
                    paddingBottom: "5px",
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "right",
                    borderTop: "1px dashed #929292",
                  }}
                >
                  Subtotal:
                </td>
                <td
                  style={{
                    paddingTop: "5px",
                    paddingBottom: "5px",
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "center",
                    borderTop: "1px dashed #929292",
                    borderRight: "1px dashed #929292",
                  }}
                  class="ng-binding"
                >
                  ${formatter.format(subtotal)}
                </td>
              </tr>
              <tr>
                <td
                  colspan="4"
                  style={{
                    paddingRight: "5px",

                    height: "45px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "right",
                    border: "1px dashed #929292",
                  }}
                >
                  <NavLink to="/cart/checkout">
                    <img
                      alt="Check Out"
                      style={{
                        cursor: "pointer",
                        float: "right",
                        padding: "0 0 0 5px",
                        width: "100px",
                      }}
                      src={Checkout_button}
                      alt="This is an img"
                    ></img>
                  </NavLink>
                  <img />
                  <NavLink
                    to="/shop"
                    style={{
                      float: "right",
                      padding: "0 5px 0 0",
                    }}
                  >
                    <img
                      alt="Continue Shopping"
                      ng-src="/app/shop/cart/images/ContinueShopping_button.png"
                      class="img-responsive"
                      src={ContinueShopping}
                    />
                  </NavLink>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      </section>
    </main>
  );
};
export default CartPage;
