import React, { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import Checkout from "../shared/img/Checkout_button.png";
import Back from "../shared/img/Back_button.png";
const CheckoutPage = () => {
  let subtotal = 0;
  //on change set the value of all fields
  const formatter = new Intl.NumberFormat("en-US", {
    minimumFractionDigits: 2,
    maximumFractionDigits: 2,
  });
  const dispatch = useDispatch();
  const cartItems = useSelector((state) => state.cartReducers);
  const cartOrderState = useSelector((state) => state.cartOrderReducers);
  const [billingFirstName, setBillingFirstName] = useState("");
  const [billingLastName, setBillingLastName] = useState("");
  const [billingAddress1, setBillingAddress1] = useState("");
  const [billingAddress2, setBillingAddress2] = useState("");
  const [billingCity, setBillingCity] = useState("");
  const [billingState, setBillingState] = useState("");
  const [billingPostalCode, setBillingPostalCode] = useState("");
  const [billingPhone, setBillingPhone] = useState("");
  const [billingEmail, setBillingEmail] = useState("");

  const [shippingFirstName, setShippingFirstName] = useState("");
  const [shippingLastName, setShippingLastName] = useState("");
  const [shippingAddress1, setShippingAddress1] = useState("");
  const [shippingAddress2, setShippingAddress2] = useState("");
  const [shippingCity, setShippingCity] = useState("");
  const [shippingState, setShippingState] = useState("");
  const [shippingPostalCode, setShippingPostalCode] = useState("");
  const [shippingPhone, setShippingPhone] = useState("");
  const [shippingEmail, setShippingEmail] = useState("");
  const [note, setNote] = useState("");

  const handleUpdateBillFirstName = (billingFirstNameInput) => {
    setBillingFirstName(billingFirstNameInput.target.value);
    console.log(billingFirstName);
  };
  const handleUpdateBillLastName = (billingLastNameInput) => {
    setBillingLastName(billingLastNameInput.target.value);
    console.log(billingLastName);
  };
  const handleUpdateBillAddress1 = (billingAddress1Input) => {
    setBillingAddress1(billingAddress1Input.target.value);
    console.log(billingAddress1);
  };
  const handleUpdateBillAddress2 = (billingAddress2Input) => {
    setBillingAddress2(billingAddress2Input.target.value);
    console.log(billingAddress2);
  };
  const handleUpdateBillCity = (billingCityInput) => {
    setBillingCity(billingCityInput.target.value);
    console.log(billingCity);
  };
  const handleUpdateBillState = (billingStateInput) => {
    setBillingState(billingStateInput.target.value);
    console.log(billingState);
  };
  const handleUpdateBillPostalCode = (billingPostalCodeInput) => {
    setBillingPostalCode(billingPostalCodeInput.target.value);
    console.log(billingPostalCode);
  };
  const handleUpdateBillPhone = (billingPhoneInput) => {
    setBillingPhone(billingPhoneInput.target.value);
    console.log(billingPhone);
  };
  const handleUpdateBillEmail = (billingEmailInput) => {
    setBillingEmail(billingEmailInput.target.value);
    console.log(billingEmail);
  };

  /* - */

  const handleUpdateShipFirstName = (shippingFirstNameInput) => {
    setShippingFirstName(shippingFirstNameInput.target.value);
    console.log(shippingFirstName);
  };
  const handleUpdateShipLastName = (shippingLastNameInput) => {
    setShippingLastName(shippingLastNameInput.target.value);
    console.log(shippingLastName);
  };
  const handleUpdateShipAddress1 = (shippingAddress1Input) => {
    setShippingAddress1(shippingAddress1Input.target.value);
    console.log(shippingAddress1);
  };
  const handleUpdateShipAddress2 = (shippingAddress2Input) => {
    setShippingAddress2(shippingAddress2Input.target.value);
    console.log(shippingAddress2);
  };
  const handleUpdateShipCity = (shippingCityInput) => {
    setShippingCity(shippingCityInput.target.value);
    console.log(shippingCity);
  };
  const handleUpdateShipState = (shippingStateInput) => {
    setShippingState(shippingStateInput.target.value);
    console.log(shippingState);
  };
  const handleUpdateShipPostalCode = (shippingPostalCodeInput) => {
    setShippingPostalCode(shippingPostalCodeInput.target.value);
    console.log(shippingPostalCode);
  };
  const handleUpdateShipPhone = (shippingPhoneInput) => {
    setShippingPhone(shippingPhoneInput.target.value);
    console.log(shippingPhone);
  };
  const handleUpdateShipEmail = (shippingEmailInput) => {
    setShippingEmail(shippingEmailInput.target.value);
    console.log(shippingEmail);
  };

  const handleUpdateNote = (noteInput) => {
    setNote(noteInput.target.value);
    console.log(note);
  };

  const checkoutClicked = () => {
    // Check if all required fields were enterd, if they were, store all values in the orderCart selectores state object
  };

  return (
    <main class=" mt-3">
      <section class="display">
        <form
          action="/Cart/StepTwo/CustomerInfoUpdate"
          method="POST"
          class="center"
        >
          <div class="row container brownCartBackground cartText">
            <p>Enter Billing & Shipping Information Step 2 of 4</p>
          </div>
          <div class="row container lightbrownCartBackground  cartText">
            <div class="col-6 pl-3">
              <div class="brownCartBackground pl-3">Billing Address</div>
              <table
                cellpadding="5"
                cellspacing="5"
                width="100%"
                class="formBorder"
              >
                <tbody>
                  <tr class="mb-5">
                    <td class="pushToRight formPadding">First Name:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="billingFirstNameInput"
                        id="billingFirstNameInput"
                        required
                        onChange={(e) => {
                          handleUpdateBillFirstName(e);
                        }}
                      />
                    </td>
                  </tr>
                  <tr>
                    <td class="pushToRight formPadding">Last Name:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="billingLastNameInput"
                        id="billingLastNameInput"
                        onChange={(e) => {
                          handleUpdateBillLastName(e);
                        }}
                        required
                      />
                    </td>
                  </tr>

                  <tr>
                    <td class="pushToRight formPadding">Address:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="billingAddress1Input"
                        id="billingAddress1Input"
                        required
                        onChange={(e) => {
                          handleUpdateBillAddress1(e);
                        }}
                      />
                    </td>
                  </tr>
                  <tr>
                    <td class="pushToRight formPadding">Address 2:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="billingAddress2Input"
                        id="billingAddress2Input"
                        onChange={(e) => {
                          handleUpdateBillAddress2(e);
                        }}
                        placeholder="Optional"
                      />
                    </td>
                  </tr>

                  <tr>
                    <td class="pushToRight formPadding">City:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="billingCityInput"
                        id="billingCityInput"
                        onChange={(e) => {
                          handleUpdateBillCity(e);
                        }}
                        required
                      />
                    </td>
                  </tr>
                  <tr>
                    <td class="pushToRight formPadding">State:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="billingStateInput"
                        id="billingStateInput"
                        onChange={(e) => {
                          handleUpdateBillState(e);
                        }}
                        required
                      />
                    </td>
                  </tr>

                  <tr>
                    <td class="pushToRight formPadding">Zip / Postal:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="billingZipInput"
                        id="billingZipInput"
                        onChange={(e) => {
                          handleUpdateBillPostalCode(e);
                        }}
                        required
                      />
                    </td>
                  </tr>
                  <tr>
                    <td class="pushToRight formPadding">Phone:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="billingPhoneInput"
                        id="billingPhoneInput"
                        onChange={(e) => {
                          handleUpdateBillPhone(e);
                        }}
                        required
                      />
                    </td>
                  </tr>
                  <tr>
                    <td class="pushToRight formPadding">Email:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="billingEmailInput"
                        id="billingEmailInput"
                        onChange={(e) => {
                          handleUpdateBillEmail(e);
                        }}
                        required
                      />
                    </td>
                  </tr>
                </tbody>
              </table>
            </div>

            <div class="col-6 pl-3">
              <div class="brownCartBackground pl-3">
                Same as Billing Address{" "}
                <input
                  type="checkbox"
                  onClick="makeBillingMatchShipping(this.checked);"
                />{" "}
                Shipping
              </div>
              <table
                cellpadding="5"
                cellspacing="5"
                width="100%"
                class="formBorder"
              >
                <tbody>
                  <tr class="mb-5">
                    <td class="pushToRight formPadding">First Name:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="firstNameInput"
                        id="firstNameInput"
                        onChange={(e) => {
                          handleUpdateShipFirstName(e);
                        }}
                        required
                      />
                    </td>
                  </tr>
                  <tr>
                    <td class="pushToRight formPadding">Last Name:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="lastNameInput"
                        id="lastNameInput"
                        onChange={(e) => {
                          handleUpdateShipLastName(e);
                        }}
                        required
                      />
                    </td>
                  </tr>

                  <tr>
                    <td class="pushToRight formPadding">Address:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="address1Input"
                        id="address1Input"
                        onChange={(e) => {
                          handleUpdateShipAddress1(e);
                        }}
                        required
                      />
                    </td>
                  </tr>
                  <tr>
                    <td class="pushToRight formPadding">Address 2:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="address2Input"
                        id="address2Input"
                        onChange={(e) => {
                          handleUpdateShipAddress2(e);
                        }}
                        placeholder="Optional"
                      />
                    </td>
                  </tr>

                  <tr>
                    <td class="pushToRight formPadding">City:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="cityInput"
                        id="cityInput"
                        onChange={(e) => {
                          handleUpdateShipCity(e);
                        }}
                        required
                      />
                    </td>
                  </tr>
                  <tr>
                    <td class="pushToRight formPadding">State:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="stateInput"
                        id="stateInput"
                        onChange={(e) => {
                          handleUpdateShipState(e);
                        }}
                        required
                      />
                    </td>
                  </tr>

                  <tr>
                    <td class="pushToRight formPadding">Zip / Postal:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="zipInput"
                        id="zipInput"
                        onChange={(e) => {
                          handleUpdateShipPostalCode(e);
                        }}
                        required
                      />
                    </td>
                  </tr>
                  <tr>
                    <td class="pushToRight formPadding">Phone:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="phoneInput"
                        id="phoneInput"
                        onChange={(e) => {
                          handleUpdateShipPhone(e);
                        }}
                        required
                      />
                    </td>
                  </tr>
                  <tr>
                    <td class="pushToRight formPadding">Email:</td>
                    <td class="formPadding">
                      <input
                        type="text"
                        name="emailInput"
                        id="emailInput"
                        onChange={(e) => {
                          handleUpdateShipEmail(e);
                        }}
                        required
                      />
                    </td>
                  </tr>
                </tbody>
              </table>
            </div>
          </div>
          <div class="row container lightbrownCartBackground cartText mt-4 ">
            <table border="0" cellpadding="0" cellspacing="0" width="100%">
              <tbody>
                <tr>
                  <td
                    style={{
                      width: "500px",
                      height: "25px",
                      padding: "5px",
                      backgroundColor: "#d2be9d",
                      color: "#4b290c",
                      fontWeight: "bold",
                      borderTop: "1px dashed #929292",
                      borderLeft: "1px dashed #929292",
                    }}
                  >
                    Shipping to You
                  </td>
                  <td
                    style={{
                      width: "500px",
                      height: "25px",
                      padding: "5px",
                      backgroundColor: "#d2be9d",
                      color: "#4b290c",
                      fontWeight: "bold",
                      borderTop: "1px dashed #929292",
                      borderLeft: "1px dashed #929292",
                    }}
                  >
                    Price
                  </td>
                  <td
                    style={{
                      width: "500px",
                      height: "25px",
                      padding: "5px",
                      backgroundColor: "#d2be9d",
                      color: "#4b290c",
                      fontWeight: "bold",
                      borderTop: "1px dashed #929292",
                      borderLeft: "1px dashed #929292",
                    }}
                  >
                    Quantity
                  </td>
                  <td
                    style={{
                      width: "100px",
                      height: "25px",
                      textAlign: "center",
                      backgroundColor: "#d2be9d",
                      color: "#4b290c",
                      fontWeight: "bold",
                      borderTop: "1px dashed #929292",
                      borderLeft: "1px dashed #929292",
                      borderRight: "1px dashed #929292",
                    }}
                  >
                    Total
                  </td>
                </tr>
                {cartItems.map((item) => (
                  <tr
                    ng-repeat="product in ctrl.shopSvc.orderCart.products"
                    class="ng-scope"
                  >
                    <td
                      style={{
                        padding: "5px",
                        color: "#4b290c",
                        borderTop: "1px dashed #929292",
                        borderLeft: "1px dashed #929292",
                        textAlign: "center",
                      }}
                      class="ng-binding"
                    >
                      {item.name}
                    </td>
                    <td
                      style={{
                        padding: "5px",
                        color: "#4b290c",
                        borderTop: "1px dashed #929292",
                        borderLeft: "1px dashed #929292",
                        textAlign: "center",
                      }}
                      class="ng-binding"
                    >
                      ${formatter.format(item.price)}
                    </td>
                    <td
                      style={{
                        padding: "5px",
                        color: "#4b290c",
                        borderTop: "1px dashed #929292",
                        borderLeft: "1px dashed #929292",
                        textAlign: "center",
                      }}
                    >
                      {item.qty}
                    </td>

                    <td
                      style={{
                        borderTop: "1px dashed #929292",
                        borderLeft: "1px dashed #929292",
                        color: "#4b290c",
                        border: "1px solid #929292; text-align:center",
                        textAlign: "center",
                        borderRight: "1px dashed #929292",
                      }}
                    >
                      <div
                        style={{
                          paddingTop: "10px",
                        }}
                        class="ng-binding"
                      >
                        ${formatter.format(item.total)}
                        <div style={{ visibility: "hidden" }}>
                          {(subtotal += item.qty * item.price)}
                        </div>
                      </div>
                    </td>
                  </tr>
                ))}

                <tr
                  style={{
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "right",
                    borderTop: "1px dashed #929292",
                  }}
                >
                  <td
                    style={{
                      textAlign: "right",
                      color: "#4b290c",
                      fontWeight: "bold",
                    }}
                  ></td>
                  <td
                    style={{
                      paddingTop: "5px",
                      height: "35px",
                      color: "#4b290c",
                      fontWeight: "bold",
                      textAlign: "center",
                    }}
                  >
                    &nbsp;
                  </td>
                  <td
                    style={{
                      height: "35px",
                      color: "#4b290c",
                      fontWeight: "bold",
                      textAlign: "right",
                    }}
                  >
                    Subtotal:
                  </td>
                  <td
                    style={{
                      height: "35px",
                      color: "#4b290c",
                      fontWeight: "bold",
                      textAlign: "center",
                    }}
                    class="ng-binding"
                  >
                    ${formatter.format(subtotal)}
                  </td>
                </tr>
              </tbody>
            </table>
          </div>

          <div class="row container lightbrownCartBackground  cartText formBorder pushToLeft">
            <div>
              <p>Comments or Additional Information</p>
            </div>
            <div class=" container lightbrownCartBackground  cartText">
              <p>
                <textarea
                  rows="4"
                  cols="50"
                  name="noteInput"
                  id="noteInput"
                  onChange={(e) => {
                    handleUpdateNote(e);
                  }}
                />
              </p>
            </div>
          </div>

          <div class="row container lightbrownCartBackground  cartText">
            <div class="col-8 center"></div>

            <div class="col-4 center ">
              <NavLink to="/Cart">
                <img src={Back} class="pr-3" alt="" />
              </NavLink>
              {/* This could be a button that triggers a functon that checks to see if any of our state thingys are empty and if they are all filled we can set the redux state to that */}
              <NavLink to="/Cart/StepThree">
                <img src={Checkout} class="pr-3" alt="" />
              </NavLink>
            </div>
          </div>
        </form>
      </section>
      {/* <!-- -------------------------------------------------------------------------------------------------------------------------------- --> */}
      <section class="display2 center">
        <div class="col-12 center  mt-4" style={{ padding: "10px" }}>
          <form
            action="/Cart/StepTwo/CustomerInfoUpdate"
            method="POST"
            style={{ width: "100%" }}
          >
            <div
              class="col lightbrownCartBackground mt-3"
              style={{ color: "black" }}
            >
              <div class=" brownCartBackground  cartText">
                <p>Payment Information Step 3 of 4</p>
              </div>

              <div class="col-12">
                <div class="center brownCartBackground">
                  <p>Billing Address</p>
                </div>
              </div>

              <div class="col-12">
                <table
                  cellpadding="5"
                  cellspacing="5"
                  width="100%"
                  class="formBorder"
                >
                  <tbody>
                    <tr class="mb-5">
                      <td class="pushToRight formPadding">First Name:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="firstNameInput"
                          id="botFirstNameInput"
                          required
                          style={{ width: "90%" }}
                        />
                      </td>
                    </tr>
                    <tr>
                      <td class="pushToRight formPadding">Last Name:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="lastNameInput"
                          id="botLastNameInput"
                          style={{ width: "90%" }}
                          required
                        />
                      </td>
                    </tr>

                    <tr>
                      <td class="pushToRight formPadding">Address:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="address1Input"
                          id="botAddress1Input"
                          style={{ width: "90%" }}
                          required
                        />
                      </td>
                    </tr>
                    <tr>
                      <td class="pushToRight formPadding">Address 2:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="address2Input"
                          id="botAddress2Input"
                          style={{ width: "90%" }}
                          placeholder="Optional"
                        />
                      </td>
                    </tr>

                    <tr>
                      <td class="pushToRight formPadding">City:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          style={{ width: "90%" }}
                          name="cityInput"
                          id="botCityInput"
                          required
                        />
                      </td>
                    </tr>
                    <tr>
                      <td class="pushToRight formPadding">State:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="stateInput"
                          id="botStateInput"
                          style={{ width: "90%" }}
                          required
                        />
                      </td>
                    </tr>

                    <tr>
                      <td class="pushToRight formPadding">Zip / Postal:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="zipInput"
                          id="botZipInput"
                          style={{ width: "90%" }}
                          required
                        />
                      </td>
                    </tr>
                    <tr>
                      <td class="pushToRight formPadding">Phone:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="phoneInput"
                          id="botPhoneInput"
                          style={{ width: "90%" }}
                          required
                        />
                      </td>
                    </tr>
                    <tr>
                      <td class="pushToRight formPadding">Email:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="emailInput"
                          id="botEmailInput"
                          style={{ width: "90%" }}
                          required
                        />
                      </td>
                    </tr>
                  </tbody>
                </table>
              </div>

              <div class="col-12">
                <div class="brownCartBackground">
                  Same as shipping.
                  <input
                    type="checkbox"
                    onClick="makeBillingMatchShipping(this.checked);"
                  />{" "}
                  Billing
                </div>
                <table
                  cellpadding="5"
                  cellspacing="5"
                  width="100%"
                  class="formBorder"
                >
                  <tbody>
                    <tr class="mb-5">
                      <td class="pushToRight formPadding">First Name:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="billingFirstNameInput"
                          style={{ width: "90%" }}
                          id="botBillingFirstNameInput"
                          required
                        />
                      </td>
                    </tr>
                    <tr>
                      <td class="pushToRight formPadding">Last Name:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="billingLastNameInput"
                          style={{ width: "90%" }}
                          id="botBillingLastNameInput"
                          required
                        />
                      </td>
                    </tr>

                    <tr>
                      <td class="pushToRight formPadding">Address:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="billingAddress1Input"
                          style={{ width: "90%" }}
                          id="botBillingAddress1Input"
                          required
                        />
                      </td>
                    </tr>
                    <tr>
                      <td class="pushToRight formPadding">Address 2:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="billingAddress2Input"
                          style={{ width: "90%" }}
                          id="botBillingAddress2Input"
                          placeholder="Optional"
                        />
                      </td>
                    </tr>

                    <tr>
                      <td class="pushToRight formPadding">City:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="billingCityInput"
                          style={{ width: "90%" }}
                          id="botBillingCityInput"
                          required
                        />
                      </td>
                    </tr>
                    <tr>
                      <td class="pushToRight formPadding">State:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="billingStateInput"
                          style={{ width: "90%" }}
                          id="botBillingStateInput"
                          required
                        />
                      </td>
                    </tr>

                    <tr>
                      <td class="pushToRight formPadding">Zip / Postal:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="billingZipInput"
                          style={{ width: "90%" }}
                          id="botBillingZipInput"
                          required
                        />
                      </td>
                    </tr>
                    <tr>
                      <td class="pushToRight formPadding">Phone:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="billingPhoneInput"
                          style={{ width: "90%" }}
                          id="botBillingPhoneInput"
                          required
                        />
                      </td>
                    </tr>
                    <tr>
                      <td class="pushToRight formPadding">Email:</td>
                      <td class="formPadding">
                        <input
                          type="text"
                          name="billingEmailInput"
                          style={{ width: "90%" }}
                          id="botBillingEmailInput"
                          required
                        />
                      </td>
                    </tr>
                  </tbody>
                </table>
              </div>
            </div>
            <div class="col-12 lightbrownCartBackground cartText mt-4">
              <table border="0" cellpadding="0" cellspacing="0" width="100%">
                <tbody>
                  <tr>
                    <td
                      style={{
                        width: "500px",
                        height: "25px",
                        padding: "5px",
                        backgroundColor: "#d2be9d",
                        color: "#4b290c",
                        fontWeight: "bold",
                        borderTop: "1px dashed #929292",
                        borderLeft: "1px dashed #929292",
                      }}
                    >
                      Shipping to You
                    </td>
                    <td
                      style={{
                        width: "500px",
                        height: "25px",
                        padding: "5px",
                        backgroundColor: "#d2be9d",
                        color: "#4b290c",
                        fontWeight: "bold",
                        borderTop: "1px dashed #929292",
                        borderLeft: "1px dashed #929292",
                      }}
                    >
                      Price
                    </td>
                    <td
                      style={{
                        width: "500px",
                        height: "25px",
                        padding: "5px",
                        backgroundColor: "#d2be9d",
                        color: "#4b290c",
                        fontWeight: "bold",
                        borderTop: "1px dashed #929292",
                        borderLeft: "1px dashed #929292",
                      }}
                    >
                      Quantity
                    </td>
                    <td
                      style={{
                        width: "100px",
                        height: "25px",
                        textAlign: "center",
                        backgroundColor: "#d2be9d",
                        color: "#4b290c",
                        fontWeight: "bold",
                        borderTop: "1px dashed #929292",
                        borderLeft: "1px dashed #929292",
                        borderRight: "1px dashed #929292",
                      }}
                    >
                      Total
                    </td>
                  </tr>
                  <tr
                    ng-repeat="product in ctrl.shopSvc.orderCart.products"
                    class="ng-scope"
                  >
                    <td
                      style={{
                        padding: "5px",
                        color: "#4b290c",
                        borderTop: "1px dashed #929292",
                        borderLeft: "1px dashed #929292",
                      }}
                      class="ng-binding"
                    >
                      Name
                    </td>
                    <td
                      style={{
                        textAlign: "center",
                        color: "#4b290c",
                        borderTop: "1px dashed #929292",
                        borderLeft: "1px dashed #929292",
                      }}
                      class="ng-binding"
                    >
                      $Price
                    </td>
                    <td
                      style={{
                        padding: "5px",
                        height: "35px",
                        textAlign: "center",
                        color: "#4b290c",
                        borderTop: "1px dashed #929292",
                        borderLeft: "1px dashed #929292",
                      }}
                    >
                      Qty
                    </td>
                    <td
                      style={{
                        textAlign: "center",
                        color: "#4b290c",
                        borderTop: "1px dashed #929292",
                        borderLeft: "1px dashed #929292",
                        borderRight: "1px dashed #929292",
                      }}
                    >
                      <div style={{ paddingTop: "10px" }} class="ng-binding">
                        $0.00
                      </div>
                      <div
                        style={{ paddingTop: "10px", paddingBottom: "5px" }}
                      ></div>
                    </td>
                  </tr>

                  <tr
                    style={{
                      height: "35px",
                      color: "#4b290c",
                      fontWeight: "bold",
                      textAlign: "right",
                      borderTop: "1px dashed #929292",
                    }}
                  >
                    <td
                      style={{
                        textAlign: "right",
                        color: "#4b290c",
                        fontWeight: "bold",
                      }}
                    ></td>
                    <td
                      style={{
                        paddingTop: "5px",
                        height: "35px",
                        color: "#4b290c",
                        fontWeight: "bold",
                        textAlign: "center",
                      }}
                    >
                      &nbsp;
                    </td>
                    <td
                      style={{
                        height: "35px",
                        color: "#4b290c",
                        fontWeight: "bold",
                        textAlign: "right",
                      }}
                    >
                      Subtotal:
                    </td>
                    <td
                      style={{
                        height: "35px",
                        color: "#4b290c",
                        fontWeight: "bold",
                        textAlign: "center",
                      }}
                      class="ng-binding"
                    >
                      0.00
                    </td>
                  </tr>
                </tbody>
              </table>

              <div class="col-12 lightbrownCartBackground cartText formBorder pushToLeft">
                <div>
                  <p>Comments or Additional Information</p>
                </div>
                <div class="  lightbrownCartBackground cartText">
                  <p>
                    <textarea
                      rows="4"
                      cols="30"
                      name="noteInput"
                      id="noteInput"
                    >
                      Note
                    </textarea>
                  </p>
                </div>
              </div>

              <div class="col-12 lightbrownCartBackground cartText">
                <div class="pushToRight ">
                  <NavLink to="/Cart">
                    <img src={Back} class="pb-4 pt-1" alt=""></img>
                  </NavLink>
                  <input type="image" src={Checkout} alt="Submit" />
                </div>
              </div>
            </div>
          </form>
        </div>
      </section>
    </main>
  );
};
export default CheckoutPage;
