import React from "react";

const ShopPageItemComponentMobile = (props) => {
  return (
    <section className="mt-1 mb-3 pl-2">
      <div
        className="dotted_line_bottom dotted_line_right content_title boarderTop "
        style={{ width: "90%" }}
      >
        {props.cookbook.Name}
      </div>
      <div className="row">
        <div className="col-6 shopDiscriptionSpacer">
          {props.cookbook.Description}
        </div>
        <div className="col-6 centerNoFlex">
          <p>Price: {props.cookbook.PriceDescription}</p>
          <input
            type="hidden"
            name="productIdInput"
            id="productIdInput"
            value={props.cookbook.ProductID}
          />
          <p>
            Quantity:
            <input
              onChange={(e) => {
                props.handleSetQty(e);
              }} /* <----- this needs to be checked to see if it contians anything that needs to be replaced */
              type="number"
              onKeyDown={(evt) => {
                if (evt.keyCode == 8) {
                  // I just have to put this check in to kinda break the if else chain so that the backspace can be allowed but if you try to add a number, it wont let you
                } else if (evt.target.value.length === 2) {
                  evt.preventDefault();
                } else if (
                  evt.key === "e" ||
                  evt.key === "-" ||
                  evt.key === "+"
                ) {
                  evt.preventDefault();
                }
              }}
              pattern="([0-9]|[0-9])"
              maxLength="2"
              min="1"
              max="99"
              style={{ width: "40px" }}
              name="qtyInput"
              id="qtyInput"
              placeholder="0"
              required
              key
            />
          </p>
          <p>
            <input
              type="image"
              alt="Add to cart"
              src={props.CartButton}
              onClick={() =>
                props.handleAddToCart(
                  props.qty,
                  props.cookbook.ProductID,
                  props.cookbook.Name,
                  props.cookbook.Price
                )
              }
            />
          </p>
          {props.cookbook.ImagePath !== "" ? (
            <img
              src={`${props.cookbook.ImagePath}`}
              alt="This is a pic"
              style={{ maxWidth: "130px" }}
            />
          ) : (
            ""
          )}
        </div>
      </div>
    </section>
  );
};
export default ShopPageItemComponentMobile;
