const initialState = {
  shippingCost: 0,
  cardType: "",
  cardNumber: "",
};

const pageThreeReducers = (state = initialState, action) => {
  switch (action.type) {
    case "STEPTHREE_UPDATE_PAGE":
      const actionPayload = {
        shippingCost: action.payload.shippingCost,
        cardType: action.payload.cardType,
        cardNumber: action.payload.cardNumber,
      };
      state = actionPayload;
      return state;
    default:
      return state;
  }
};

export default pageThreeReducers;
