import React, { useState } from "react";

import { NavLink, useHistory } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { CHECKOUT_UPDATE_PAGE } from "../../redux/actions";
import axios from "axios";
import Checkout from "../shared/img/Checkout_button.png";
import Back from "../shared/img/Back_button.png";
const CheckoutPage = () => {
  const history = useHistory();
  let subtotal = 0;
  //on change set the value of all fields
  const formatter = new Intl.NumberFormat("en-US", {
    minimumFractionDigits: 2,
    maximumFractionDigits: 2,
  });
  const dispatch = useDispatch();
  const cartItems = useSelector((state) => state.cartReducers);
  const cartOrderState = useSelector((state) => state.orderCartReducers);
  const sessionID = useSelector((state) => state.sessionReducers);

  const [billingFirstName, setBillingFirstName] = useState(
    cartOrderState.billingFirstName
  );
  const [billingLastName, setBillingLastName] = useState(
    cartOrderState.billingLastName
  );
  const [billingAddress1, setBillingAddress1] = useState(
    cartOrderState.billingAddress1
  );
  const [billingAddress2, setBillingAddress2] = useState(
    cartOrderState.billingAddress2
  );
  const [billingCity, setBillingCity] = useState(cartOrderState.billingCity);
  const [billingState, setBillingState] = useState(cartOrderState.billingState);
  const [billingPostalCode, setBillingPostalCode] = useState(
    cartOrderState.billingPostalCode
  );
  const [billingPhone, setBillingPhone] = useState(cartOrderState.billingPhone);
  const [billingEmail, setBillingEmail] = useState(cartOrderState.billingEmail);

  const [shippingFirstName, setShippingFirstName] = useState(
    cartOrderState.shippingFirstName
  );
  const [shippingLastName, setShippingLastName] = useState(
    cartOrderState.shippingLastName
  );
  const [shippingAddress1, setShippingAddress1] = useState(
    cartOrderState.shippingAddress1
  );
  const [shippingAddress2, setShippingAddress2] = useState(
    cartOrderState.shippingAddress2
  );
  const [shippingCity, setShippingCity] = useState(cartOrderState.shippingCity);
  const [shippingState, setShippingState] = useState(
    cartOrderState.shippingState
  );
  const [shippingPostalCode, setShippingPostalCode] = useState(
    cartOrderState.shippingPostalCode
  );
  const [shippingPhone, setShippingPhone] = useState(
    cartOrderState.shippingPhone
  );
  const [shippingEmail, setShippingEmail] = useState(
    cartOrderState.shippingEmail
  );
  const [note, setNote] = useState(cartOrderState.note);

  const handleUpdateBillFirstName = (billingFirstNameInput) => {
    setBillingFirstName(billingFirstNameInput.target.value);
  };
  const handleUpdateBillLastName = (billingLastNameInput) => {
    setBillingLastName(billingLastNameInput.target.value);
  };
  const handleUpdateBillAddress1 = (billingAddress1Input) => {
    setBillingAddress1(billingAddress1Input.target.value);
  };
  const handleUpdateBillAddress2 = (billingAddress2Input) => {
    setBillingAddress2(billingAddress2Input.target.value);
  };
  const handleUpdateBillCity = (billingCityInput) => {
    setBillingCity(billingCityInput.target.value);
  };
  const handleUpdateBillState = (billingStateInput) => {
    setBillingState(billingStateInput.target.value);
  };
  const handleUpdateBillPostalCode = (billingPostalCodeInput) => {
    setBillingPostalCode(billingPostalCodeInput.target.value);
  };
  const handleUpdateBillPhone = (billingPhoneInput) => {
    setBillingPhone(billingPhoneInput.target.value);
  };
  const handleUpdateBillEmail = (billingEmailInput) => {
    setBillingEmail(billingEmailInput.target.value);
  };

  /* - */

  const handleUpdateShipFirstName = (shippingFirstNameInput) => {
    setShippingFirstName(shippingFirstNameInput.target.value);
  };
  const handleUpdateShipLastName = (shippingLastNameInput) => {
    setShippingLastName(shippingLastNameInput.target.value);
  };
  const handleUpdateShipAddress1 = (shippingAddress1Input) => {
    setShippingAddress1(shippingAddress1Input.target.value);
  };
  const handleUpdateShipAddress2 = (shippingAddress2Input) => {
    setShippingAddress2(shippingAddress2Input.target.value);
  };
  const handleUpdateShipCity = (shippingCityInput) => {
    setShippingCity(shippingCityInput.target.value);
  };
  const handleUpdateShipState = (shippingStateInput) => {
    setShippingState(shippingStateInput.target.value);
  };
  const handleUpdateShipPostalCode = (shippingPostalCodeInput) => {
    setShippingPostalCode(shippingPostalCodeInput.target.value);
  };
  const handleUpdateShipPhone = (shippingPhoneInput) => {
    setShippingPhone(shippingPhoneInput.target.value);
  };
  const handleUpdateShipEmail = (shippingEmailInput) => {
    setShippingEmail(shippingEmailInput.target.value);
  };

  const handleUpdateNote = (noteInput) => {
    setNote(noteInput.target.value);
  };

  const checkoutClicked = () => {
    // Check if all required fields were enterd, if they were, store all values in the orderCart selectores state object
    if (
      billingFirstName === "" ||
      billingLastName === "" ||
      billingAddress1 === "" ||
      billingCity === "" ||
      billingState === "" ||
      billingPostalCode === "" ||
      billingPhone === "" ||
      billingEmail === "" ||
      shippingFirstName === "" ||
      shippingLastName === "" ||
      shippingAddress1 === "" ||
      shippingCity === "" ||
      shippingState === "" ||
      shippingPostalCode === ""
    ) {
    } else {
      dispatch(
        CHECKOUT_UPDATE_PAGE(
          billingFirstName,
          billingLastName,
          billingAddress1,
          billingAddress2,
          billingCity,
          billingState,
          billingPostalCode,
          billingPhone,
          billingEmail,
          shippingFirstName,
          shippingLastName,
          shippingAddress1,
          shippingAddress2,
          shippingCity,
          shippingState,
          shippingPostalCode,
          shippingPhone,
          shippingEmail,
          note
        )
      );
      //Take the state and do an api call and update with http://localhost:5000/api/cart/checkout/update

      const updateDB = async () => {
        await axios.patch("http://localhost:5000/api/cart/checkout/update", {
          billingFirstNameInput: billingFirstName,
          billingLastNameInput: billingLastName,
          billingAddress1Input: billingAddress1,
          billingAddress2Input: billingAddress2,
          billingCityInput: billingCity,
          billingStateInput: billingState,
          billingPostalCodeInput: billingPostalCode,
          billingPhoneInput: billingPhone,
          billingEmailInput: billingEmail,
          shippingFirstNameInput: shippingFirstName,
          shippingLastNameInput: shippingLastName,
          shippingAddress1Input: shippingAddress1,
          shippingAddress2Input: shippingAddress2,
          shippingCityInput: shippingCity,
          shippingStateInput: shippingState,
          shippingPostalCodeInput: shippingPostalCode,
          shippingPhoneInput: shippingPhone,
          shippingEmailInput: shippingEmail,
          notesInput: note,
          sessionIdInput: sessionID,
        });
      };
      updateDB();
      history.push("/cart/stepthree");
    }
  };
  const sameAsBillingClicked = () => {
    setShippingFirstName(billingFirstName);
    setShippingLastName(billingLastName);
    setShippingAddress1(billingAddress1);
    setShippingAddress2(billingAddress2);
    setShippingCity(billingCity);
    setShippingState(billingState);
    setShippingPostalCode(billingPostalCode);
    setShippingPhone(billingPhone);
    setShippingEmail(billingEmail);
  };

  return (
    <main className=" ">
      <section className="display">
        <form
          action="/Cart/StepTwo/CustomerInfoUpdate"
          method="POST"
          className="center"
        >
          <div className="row container brownCartBackground cartText">
            <p>Enter Billing & Shipping Information Step 2 of 4</p>
          </div>
          <div className="row container lightbrownCartBackground  cartText">
            <div className="col-6 pl-3">
              <div className="brownCartBackground pl-3">Billing Address</div>
              <table
                cellpadding="5"
                cellspacing="5"
                width="100%"
                className="formBorder"
              >
                <tbody>
                  <tr className="mb-5">
                    <td className="pushToRight formPadding">First Name:</td>
                    <td className="formPadding">
                      <input
                        type="text"
                        name="billingFirstNameInput"
                        id="billingFirstNameInput"
                        required
                        value={billingFirstName}
                        onChange={(e) => {
                          handleUpdateBillFirstName(e);
                        }}
                      />
                    </td>
                  </tr>
                  <tr>
                    <td className="pushToRight formPadding">Last Name:</td>
                    <td className="formPadding">
                      <input
                        type="text"
                        name="billingLastNameInput"
                        id="billingLastNameInput"
                        value={billingLastName}
                        onChange={(e) => {
                          handleUpdateBillLastName(e);
                        }}
                        required
                      />
                    </td>
                  </tr>

                  <tr>
                    <td className="pushToRight formPadding">Address:</td>
                    <td className="formPadding">
                      <input
                        type="text"
                        name="billingAddress1Input"
                        id="billingAddress1Input"
                        required
                        value={billingAddress1}
                        onChange={(e) => {
                          handleUpdateBillAddress1(e);
                        }}
                      />
                    </td>
                  </tr>
                  <tr>
                    <td className="pushToRight formPadding">Address 2:</td>
                    <td className="formPadding">
                      <input
                        type="text"
                        name="billingAddress2Input"
                        id="billingAddress2Input"
                        value={billingAddress2}
                        onChange={(e) => {
                          handleUpdateBillAddress2(e);
                        }}
                        placeholder="Optional"
                      />
                    </td>
                  </tr>

                  <tr>
                    <td className="pushToRight formPadding">City:</td>
                    <td className="formPadding">
                      <input
                        type="text"
                        name="billingCityInput"
                        id="billingCityInput"
                        value={billingCity}
                        onChange={(e) => {
                          handleUpdateBillCity(e);
                        }}
                        required
                      />
                    </td>
                  </tr>
                  <tr>
                    <td className="pushToRight formPadding">State:</td>
                    <td className="formPadding">
                      <input
                        type="text"
                        name="billingStateInput"
                        id="billingStateInput"
                        value={billingState}
                        onKeyDown={(evt) => {
                          if (evt.keyCode === 8) {
                            // Break
                          } else if (evt.target.value.length === 10) {
                            evt.preventDefault();
                          }
                        }}
                        maxLength="10"
                        onChange={(e) => {
                          handleUpdateBillState(e);
                        }}
                        required
                      />
                    </td>
                  </tr>

                  <tr>
                    <td className="pushToRight formPadding">Zip / Postal:</td>
                    <td className="formPadding">
                      <input
                        type="text"
                        name="billingZipInput"
                        id="billingZipInput"
                        value={billingPostalCode}
                        maxLength="20"
                        onKeyDown={(evt) => {
                          if (evt.keyCode === 8) {
                            // Break
                          } else if (evt.target.value.length === 20) {
                            evt.preventDefault();
                          }
                        }}
                        onChange={(e) => {
                          handleUpdateBillPostalCode(e);
                        }}
                        required
                      />
                    </td>
                  </tr>
                  <tr>
                    <td className="pushToRight formPadding">Phone:</td>
                    <td className="formPadding">
                      <input
                        type="text"
                        name="billingPhoneInput"
                        id="billingPhoneInput"
                        value={billingPhone}
                        onKeyDown={(evt) => {
                          if (evt.keyCode === 8) {
                            // Break
                          } else if (evt.target.value.length === 20) {
                            evt.preventDefault();
                          }
                        }}
                        onChange={(e) => {
                          handleUpdateBillPhone(e);
                        }}
                        maxLength="20"
                        required
                      />
                    </td>
                  </tr>
                  <tr>
                    <td className="pushToRight formPadding">Email:</td>
                    <td className="formPadding">
                      <input
                        type="text"
                        name="billingEmailInput"
                        id="billingEmailInput"
                        value={billingEmail}
                        onChange={(e) => {
                          handleUpdateBillEmail(e);
                        }}
                        required
                      />
                    </td>
                  </tr>
                </tbody>
              </table>
            </div>

            <div className="col-6 pl-3">
              <div className="brownCartBackground pl-3">
                Same as Billing Address{" "}
                <input
                  type="checkbox"
                  onChange={(e) => {
                    sameAsBillingClicked(e);
                  }}
                />
                Shipping
              </div>
              <table
                cellpadding="5"
                cellspacing="5"
                width="100%"
                className="formBorder"
              >
                <tbody>
                  <tr className="mb-5">
                    <td className="pushToRight formPadding">First Name:</td>
                    <td className="formPadding">
                      <input
                        type="text"
                        name="firstNameInput"
                        id="firstNameInput"
                        value={shippingFirstName}
                        onChange={(e) => {
                          handleUpdateShipFirstName(e);
                        }}
                        required
                      />
                    </td>
                  </tr>
                  <tr>
                    <td className="pushToRight formPadding">Last Name:</td>
                    <td className="formPadding">
                      <input
                        type="text"
                        name="lastNameInput"
                        id="lastNameInput"
                        value={shippingLastName}
                        onChange={(e) => {
                          handleUpdateShipLastName(e);
                        }}
                        required
                      />
                    </td>
                  </tr>

                  <tr>
                    <td className="pushToRight formPadding">Address:</td>
                    <td className="formPadding">
                      <input
                        type="text"
                        name="address1Input"
                        id="address1Input"
                        value={shippingAddress1}
                        onChange={(e) => {
                          handleUpdateShipAddress1(e);
                        }}
                        required
                      />
                    </td>
                  </tr>
                  <tr>
                    <td className="pushToRight formPadding">Address 2:</td>
                    <td className="formPadding">
                      <input
                        type="text"
                        name="address2Input"
                        id="address2Input"
                        value={shippingAddress2}
                        onChange={(e) => {
                          handleUpdateShipAddress2(e);
                        }}
                        placeholder="Optional"
                      />
                    </td>
                  </tr>

                  <tr>
                    <td className="pushToRight formPadding">City:</td>
                    <td className="formPadding">
                      <input
                        type="text"
                        name="cityInput"
                        id="cityInput"
                        value={shippingCity}
                        onChange={(e) => {
                          handleUpdateShipCity(e);
                        }}
                        required
                      />
                    </td>
                  </tr>
                  <tr>
                    <td className="pushToRight formPadding">State:</td>
                    <td className="formPadding">
                      <input
                        type="text"
                        name="stateInput"
                        id="stateInput"
                        value={shippingState}
