import { Promise } from "mssql";
import axios from "axios";
const initialState = [];

const cartReducers = (state = initialState, action) => {
  switch (action.type) {
    case "ADD_TO_CART":
      return state; //Take qty and product id and activate a function that pushes it to the cart reducer
    case "CART_LOAD_PAGE":
      state = initialState;
      const getItemsInCart = async () => {
        const itemsInCart = axios.get("http://localhost:5000/api/cart/list");
        const a = await itemsInCart;
        console.log("This is line 15 in the reducers page: ", a.data);
        let array = state;
        array.push(action.payload);
        return a.data;
      };
      return getItemsInCart();
    default:
      return state;
  }
};

export default cartReducers;
