import { Promise } from "mssql";
import axios from "axios";
const initialState = {
  billingFistName: "",
  billingLastName: "",
  billingAddress1: "",
  billingAddress2: "",
  billingCity: "",
  billingState: "",
  billingPostalCode: "",
  billingPhone: "",
  billingEmail: "",
  shippingFistName: "",
  shippingLastName: "",
  shippingAddress1: "",
  shippingAddress2: "",
  shippingCity: "",
  shippingState: "",
  shippingPostalCode: "",
  shippingPhone: "",
  shippingEmail: "",
};

const orderCartReducers = (state = initialState, action) => {
  let array = [];
  switch (action.type) {
    case "CHECKOUT_UPDATE_PAGE":
      state = action.payload;
      return state;
    default:
      return state;
  }
};

export default orderCartReducers;
