import { Promise } from "mssql";
import axios from "axios";
const initialState = [];

const cartReducers = (state = initialState, action) => {
  let array = state;
  switch (action.type) {
    case "ADD_TO_CART":
      array.push(action.payload);
      return state;
    case "CART_LOAD_PAGE":
      const fetchData = async () => {
        const result = await axios("http://localhost:5000/api/cart/list");
        console.log("line 16 cartReducers: ", result.data);
        return result.data;
      };
      fetchData();
      return array;
    default:
      return state;
  }
};

export default cartReducers;
