import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { ADD_TO_CART } from "../../redux/actions";
import axios from "axios";

import { NavLink } from "react-router-dom";
import CartButton from "../shared/img/Cart_Button_main.png";
import BakingPlanks_Header from "../shared/img/BakingPlanks_Header.png";
import Shop_MenuHeader from "../shared/img/Shop_MenuHeader.png";

const BakingPlanksPage = () => {
  const [bakingPlanks, setBakingPlanks] = useState([]);
  const [qty, setQty] = useState(0);
  const dispatch = useDispatch();

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios("http://localhost:5000/api/products/4");

      setBakingPlanks(result.data);
    };
    fetchData();
  }, []);

  const handleSetQty = (qty) => {
    setQty(qty.target.value.split(/\D/).join(""));
  };
  const sessionID = useSelector((state) => state.sessionReducers);
  const handleAddToCart = (qty, ProductID, Name, Price) => {
    setQty(qty);
    dispatch(ADD_TO_CART(qty, ProductID, Name, Price));

    const addToCartAxios = async () => {
      const thisIsTheDataWeArePosting = {
        sessionId: sessionID /* THIS IS WHERE THE SESSION ID NEEDS TO GO TO GET SENT AS A REQ.BODY.UNIQEIDENTIFIER */,
        productIdInput: ProductID,
        qtyInput: qty,
      };
      await axios
        .post("http://localhost:5000/api/cart/add", thisIsTheDataWeArePosting)
        .then(function (response) {
          console.log("thisIsTheDataWeArePutting", response);
        })
        .catch(function (error) {
          console.log(error);
        });
    };
    addToCartAxios();
  };

  return (
    <main className="center  mt-3">
      <div className="row">
        <div className="dropdown col-12 display2 text-center center">
          <button
            className="btn dropdown-toggle"
            type="button"
            id="dropdownMenuButton"
            data-toggle="dropdown"
            aria-haspopup="true"
            aria-expanded="false"
          >
            <h2>Shop</h2>
          </button>
          <div className="dropdown-menu " aria-labelledby="dropdownMenuButton">
            <NavLink
              className="nav-link-bottom boarderBottom dropdown-item"
              to="/Shop/SpiceRubs"
            >
              SPICE RUBS
            </NavLink>
            <NavLink
              className="nav-link-bottom boarderBottom dropdown-item"
              to="/Shop/Cookbooks"
            >
              COOKBOOKS
            </NavLink>
            <NavLink
              className="nav-link-bottom boarderBottom dropdown-item"
              to="/Shop/BakingPlanks"
            >
              BAKING PLANKS
            </NavLink>
            <NavLink
              className="nav-link-bottom boarderBottom dropdown-item"
              to="/Shop/BBQPlanks"
            >
              BBQ PLANKS
            </NavLink>
            <NavLink
              className="nav-link-bottom boarderBottom dropdown-item"
              to="/Shop/NutDriver "
            >
              NUT DRIVER
            </NavLink>
          </div>
        </div>

        <section className="col-4 display size14Font center">
          <ul className="hideUlDot ">
            <img src={Shop_MenuHeader} alt="This is a pic" />
            <NavLink
              className="nav-link-bottom boarderBottom"
              to="/Shop/SpiceRubs"
            >
              SPICE RUBS
            </NavLink>

            <NavLink
              className="nav-link-bottom boarderBottom"
              to="/Shop/Cookbooks"
            >
              COOKBOOKS
            </NavLink>

            <NavLink
              className="nav-link-bottom boarderBottom"
              to="/Shop/BakingPlanks"
            >
              BAKING PLANKS
            </NavLink>
            <NavLink
              className="nav-link-bottom boarderBottom"
              to="/Shop/BBQPlanks"
            >
              BBQ PLANKS
            </NavLink>
            <NavLink
              className="nav-link-bottom boarderBottom"
              to="/Shop/NutDriver"
            >
              NUT DRIVERS
            </NavLink>
          </ul>
        </section>
        <section className="col-sm-12 col-md-8 display2 mb-5 ">
          <div className="container center">
            <img
              src={BakingPlanks_Header}
              className="img-fluid"
              alt="This is a pic"
            />

            <div className="size14Font">
              <p className="mt-1 mb-3">
                Cedar and Alder planks impart a subtle yet full flavored aroma
                to anything roasted on them. Our planks are made from clear kiln
                dried Western Red Cedar and Alder. Cedar roasting planks come in
                two sizes. Alder planks are available in one size only.
              </p>

              {bakingPlanks.map((bakingPlank) => (
                <section className="mt-1 mb-3 pl-2">
                  {bakingPlank.imagePath === null ? (
                    <div>
                      {bakingPlank.name === "Large Cedar Plank" ? (
                        <div>
                          <div className="dotted_line_bottom dotted_line_right content_title boarderTop ">
                            <div
                              style={{ maxWidth: "451px", extAlign: "left" }}
                            >
                              {bakingPlank.name}
                            </div>
                          </div>
                          <div
                            className=" pl-4 row"
                            style={{ textAlign: "left" }}
                          >
                            <div className="row">
                              <div className=" shopDiscriptionSpacer">
                                <div>
                                  {" "}
                                  <p>{bakingPlank.description}</p>
                                </div>
                              </div>
                            </div>
                          </div>
                          <div className="row">
                            <div className=" shopDiscriptionSpacer">
                              <div>
                                {" "}
                                <p style={{ fontSize: "20px" }}>
                                  No Large Cedar Planks are available for
                                  purchase.
                                </p>
                              </div>
                            </div>
                          </div>
                        </div>
                      ) : (
                        <div>
                          <div
                            className="dotted_line_bottom dotted_line_right content_title boarderTop "
                            style={{ textAlign: "left" }}
                          >
                            {bakingPlank.name}
                          </div>
                          <div className="row">
                            <div className="col-6 shopDiscriptionSpacer">
                              <p>{bakingPlank.description}</p>
                              <div>
                                {" "}
                                {bakingPlank.imagePath === null ? (
                                  ""
                                ) : (
                                  <img
                                    src={`${bakingPlank.imagePath}`}
                                    alt="This is a pic"
                                    style={{ width: "110px" }}
                                  />
                                )}
                              </div>
                            </div>

                            <div className="col-6 centerNoFlex">
                              <p>Price: {bakingPlank.priceDescription}</p>
                              <input
                                type="hidden"
                                name="productIdInput"
                                id="productIdInput"
                                value={bakingPlank.productId}
                              />
                              <p>
                                Quantity:
                                <input
                                  onChange={(e) => {
                                    handleSetQty(e);
                                  }} /* <----- this needs to be checked to see if it contians anything that needs to be replaced */
                                  type="number"
                                  onKeyDown={(evt) => {
                                    if (evt.keyCode === 8) {
                                      // I just have to put this check in to kinda break the if else chain so that the backspace can be allowed but if you try to add a number, it wont let you
                                    } else if (evt.target.value.length === 2) {
                                      evt.preventDefault();
                                    } else if (
                                      evt.key === "e" ||
                                      evt.key === "-" ||
                                      evt.key === "+"
                                    ) {
                                      evt.preventDefault();
                                    }
                                  }}
                                  pattern="([0-9]|[0-9])"
                                  maxLength="2"
                                  min="1"
                                  max="99"
                                  style={{ width: "40px" }}
                                  name="qtyInput"
                                  id="qtyInput"
                                  placeholder="0"
                                  required
                                  key
                                />
                              </p>
                              <p>
                                <input
                                  type="image"
                                  alt="Add to cart"
                                  src={CartButton}
                                  onClick={() =>
                                    handleAddToCart(
                                      qty,
                                      bakingPlank.productId,
                                      bakingPlank.name,
                                      bakingPlank.price
                                    )
                                  }
                                />
                              </p>
                            </div>
                          </div>
                        </div>
                      )}
                    </div>
                  ) : (
                    <div>
                      <div
                        className="dotted_line_bottom dotted_line_right content_title boarderTop "
                        style={{ textAlign: "left" }}
                      >
                        {bakingPlank.name}
                      </div>
                      <div className="row">
                        <div className="col-6 shopDiscriptionSpacer">
                          <p>{bakingPlank.description}</p>
                          <div>
                            {" "}
                            {bakingPlank.imagePath === null ? (
                              ""
                            ) : (
                              <div>
                                <div>{bakingPlank.name}</div>
                                <div>
                                  <img
                                    src={`${bakingPlank.imagePath}`}
                                    alt="This is a pic"
                                    style={{ width: "110px" }}
                                  />
                                </div>
                              </div>
                            )}
                          </div>
                        </div>

                        <div className="col-6 centerNoFlex">
                          <p>Price: {bakingPlank.priceDescription}</p>
                          <input
                            type="hidden"
                            name="productIdInput"
                            id="productIdInput"
                            value={bakingPlank.productId}
                          />
                          <p>
                            Quantity:
                            <input
                              onChange={(e) => {
                                handleSetQty(e);
                              }}
                              type="number"
                              onKeyDown={(evt) => {
                                if (evt.keyCode === 8) {
                                } else if (evt.target.value.length === 2) {
                                  evt.preventDefault();
                                } else if (
                                  evt.key === "e" ||
                                  evt.key === "-" ||
                                  evt.key === "+"
                                ) {
                                  evt.preventDefault();
                                }
                              }}
                              pattern="([0-9]|[0-9])"
                              maxLength="2"
                              min="1"
                              max="99"
                              style={{ width: "40px" }}
                              name="qtyInput"
                              id="qtyInput"
                              placeholder="0"
                              required
                              key
                            />
                          </p>
                          <p>
                            <input
                              type="image"
                              alt="Add to cart"
                              src={CartButton}
                              onClick={() =>
                                handleAddToCart(
                                  qty,
                                  bakingPlank.productId,
                                  bakingPlank.name,
                                  bakingPlank.price
                                )
                              }
                            />
                          </p>
                        </div>
                      </div>
                    </div>
                  )}
                </section>
              ))}
            </div>
          </div>
        </section>

        <section className="col-sm-12 col-md-8 display ">
          <div className="pushToLeft row">
            <div className="boardBackgroundShop">
              <img
                src={BakingPlanks_Header}
                className="mainRightSidePicSize"
                alt="This is a pic"
                style={{ width: "103%" }}
              ></img>
            </div>
            <div className="paperBackground">
              <div className="size14Font mr-5 pl-4 mt-1">
                <p className="mt-1 mb-3 pl-2">
                  Cedar and Alder planks impart a subtle yet full flavored aroma
                  to anything roasted on them. Our planks are made from clear
                  kiln dried Western Red Cedar and Alder. Cedar roasting planks
                  come in two sizes. Alder planks are available in one size
                  only.
                </p>
                {bakingPlanks.map((bakingPlank) => (
                  <section className="mt-1 mb-3 pl-2">
                    {bakingPlank.imagePath === null ? (
                      <div>
                        {bakingPlank.name === "Large Cedar Plank" ? (
                          <div>
                            <div className="dotted_line_bottom shopDottedSections content_title">
                              {bakingPlank.name}
                            </div>
                            <p>{bakingPlank.description}</p>
                            <p
                              style={{ fontSize: "20px", textAlign: "center" }}
                            >
                              No Large Cedar Planks are available for purchase.
                            </p>
                          </div>
                        ) : (
                          <div>
                            <div className="dotted_line_bottom shopDottedSections content_title">
                              {bakingPlank.name}
                            </div>
                            <p>{bakingPlank.description}</p>{" "}
                            <input
                              type="hidden"
                              name="productIdInput"
                              id="productIdInput"
                              value={bakingPlank.productId}
                            />
                            <div className="row">
                              <div className="col-6 shopDiscriptionSpacer">
                                <p> {bakingPlank.name}</p>
                                <img
                                  src={bakingPlank.imagePath}
                                  alt={bakingPlank.imagePath}
                                />
                              </div>
                              <div className="col-6 centerNoFlex">
                                <p>Price: ${bakingPlank.priceDescription}</p>
                                <p>
                                  Quantity:
