import { Promise } from "mssql";
import axios from "axios";

const fetchData = async () => {
  const result = await axios("http://localhost:5000/api/cart/list");
  console.log("line 14 cartReducers: ", result.data);
  return result.data;
};

const initialState = fetchData();
const cartReducers = (state = initialState, action) => {
  let array = state;
  switch (action.type) {
    case "ADD_TO_CART":
      array.push(action.payload);
      return state;
    case "CART_LOAD_PAGE":
      return array;
    default:
      return state;
  }
};

export default cartReducers;
