import React, { useState } from "react";

const ContactPage = () => {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");

  const submitForm = (firstName, lastName, email, message) => {};

  return (
    <main className="container mt-3">
      <center>
        <div className="row w-100 align-items-center my-5">
          <div className="col-12 col-lg-6">
            <div className="frontTitle">
              <h1>Want to get in contact with me?</h1>
              <br />
            </div>
            <div className="frontText">
              This is a greate way for you to reach out to me if you like my
              website or have qustions.
            </div>
          </div>
          <div className="col-12 col-lg-6 text-center mt-5 mt-lg-0">
            <div className="mb-3">
              <div className="form-group">
                <label for="inputName">Your First Name:</label>
                <input
                  type="text"
                  className="form-control mb-2"
                  id="inputFirstName"
                  aria-describedby="firstNameHelp"
                  placeholder="Enter first name"
                  name="inputFirstName"
                  value={firstName}
                  onChange={(e) => {
                    setFirstName();
                  }}
                  required
                />
                <label for="inputName">Your Last Name:</label>
                <input
                  type="text"
                  value={lastName}
                  className="form-control mb-2"
                  id="inputLastName"
                  aria-describedby="lastNameHelp"
                  placeholder="Enter last name"
                  name="inputLastName"
                  onChange={(e) => {
                    setLastName();
                  }}
                  required
                />
                <label for="inputEmail">Your Email Address:</label>
                <input
                  type="text"
                  className="form-control"
                  value={email}
                  onChange={(e) => {
                    setEmail();
                  }}
                  id="inputEmail"
                  aria-describedby="emailHelp"
                  placeholder="Enter email"
                  name="inputEmail"
                  required
                />
                <small id="emailHelp" className="form-text text-muted">
                  I'll never share your email with anyone else.
                </small>
              </div>
              <div className="form-group">
                <label for="inputTextArea">Your Message:</label>
                <textarea
                  value={message}
                  className="form-control"
                  placeholder="Enter message"
                  id="inputTextArea"
                  name="inputTextArea"
                  onChange={(e) => {
                    setMessage();
                  }}
                  rows="7"
                  required
                ></textarea>
              </div>
              <button
                type="submit"
                className="btn btn-primary"
                onClick={(e) => {
                  submitForm();
                }}
              >
                Send Message
              </button>
            </div>
          </div>
        </div>
      </center>
    </main>
  );
};
export default ContactPage;
