import axios from "axios";

export const ADD_TO_CART = (qty = 1, ProductID = 2, Name = "", Price = 0) => {
  return {
    type: "ADD_TO_CART",
    payload: { qty, ProductID, Name, Price },
  };
};

export const COOKBOOK_LOAD_ITEMS = () => {
  return {
    type: "COOKBOOK_LOAD_ITEMS",
  };
};

export const CART_LOAD_PAGE = () => {
  return (dispatch) => {
    setTimeout(() => {
      const itemsInCart = axios
        .get("http://localhost:5000/api/cart/list")
        .then((res) => res.json())
        .then((cartItems) => cartItems.map(cartItems));
      dispatch(increment());
    }, 1000);
  };
};
