const initialState = {
  shippingMethode: "",
  cardType: "",
  total: 0,
  tax: 5,
};

const pageThreeReducers = (state = initialState, action) => {
  switch (action.type) {
    case "STEPTHREE_UPDATE_PAGE":
      const actionPayload = {
        shippingMethode: action.payload.shippingMethode,
        cardType: action.payload.cardType,
        total: action.payload.total,
      };
      state = actionPayload;
      return state;
    default:
      return state;
  }
};

export default pageThreeReducers;
