  billingAddress1: "",
  billingAddress2: "",
  billingCity: "",
  billingState: "",
  billingPostalCode: "",
  billingPhone: "",
  billingEmail: "",
};

const orderCartReducers = (state = initialState, action) => {
  let array = [];
  switch (action.type) {
    case "CHECKOUT_UPDATE_PAGE":
      state = action.payload;
      return state;
    default:
      return state;
  }
};

export default orderCartReducers;
