import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { ADD_TO_CART, COOKBOOK_LOAD_ITEMS } from "../../redux/actions";

import ShopPageItemComponentMobile from "./ShopPageItemComponentMobile";
import ShopPageItemComponentDesktop from "./ShopPageItemComponentDesktop";
import { NavLink } from "react-router-dom";
import CartButton from "../shared/img/Cart_Button_main.png";

import Cookbooks_Header from "../shared/img/Cookbooks_Header.png";
const NutDriverPage = () => {
  const [nutDrivers, setNutDriver] = useState([]);
  const [qty, setQty] = useState(0);
  const dispatch = useDispatch();
  const incomingItems = useSelector((state) => state.itemsReducers);

  useEffect(() => {
    dispatch(COOKBOOK_LOAD_ITEMS());
    setNutDriver(incomingItems);
  }, []);

  const handleSetQty = (qty) => {
    setQty(qty.target.value.split(/\D/).join(""));
  };

  const handleAddToCart = (qty, ProductID, Name, Price) => {
    setQty(qty);
    dispatch(ADD_TO_CART(qty, ProductID, Name, Price));
    console.log(qty, ProductID, Name, Price);
  };

  return (
    <main className="pl-3 pr-3 fitContent  mt-3">
      <div className="row">
        <div className="dropdown col-12 display2 text-center center">
          <button
            className="btn dropdown-toggle"
            type="button"
            id="dropdownMenuButton"
            data-toggle="dropdown"
            aria-haspopup="true"
            aria-expanded="false"
          >
            <h2>Shop</h2>
          </button>
          <div className="dropdown-menu " aria-labelledby="dropdownMenuButton">
            <NavLink
              className="nav-link-bottom boarderBottom dropdown-item"
              to="/Shop/SpiceRubs"
            >
              SPICE RUBS
            </NavLink>
            <NavLink
              className="nav-link-bottom boarderBottom dropdown-item"
              to="/Shop/Cookbooks"
            >
              COOKBOOKS
            </NavLink>
            <NavLink
              className="nav-link-bottom boarderBottom dropdown-item"
              to="/Shop/BakingPlanks"
            >
              BAKING PLANKS
            </NavLink>
            <NavLink
              className="nav-link-bottom boarderBottom dropdown-item"
              to="/Shop/BBQPlanks"
            >
              BBQ PLANKS
            </NavLink>
            <NavLink
              className="nav-link-bottom boarderBottom dropdown-item"
              to="/Shop/NutDriver "
            >
              NUT DRIVER
            </NavLink>
          </div>
        </div>

        <section className="col-4 display size14Font center">
          <ul className="hideUlDot ">
            <img src={Cookbooks_Header} alt="This is a pic" />
            <NavLink
              className="nav-link-bottom boarderBottom"
              to="/Shop/SpiceRubs"
            >
              SPICE RUBS
            </NavLink>

            <NavLink
              className="nav-link-bottom boarderBottom"
              to="/Shop/Cookbooks"
            >
              COOKBOOKS
            </NavLink>

            <NavLink
              className="nav-link-bottom boarderBottom"
              to="/Shop/BakingPlanks"
            >
              BAKING PLANKS
            </NavLink>
            <NavLink
              className="nav-link-bottom boarderBottom"
              to="/Shop/BBQPlanks"
            >
              BBQ PLANKS
            </NavLink>
            <NavLink
              className="nav-link-bottom boarderBottom"
              to="/Shop/NutDriver"
            >
              NUT DRIVERS
            </NavLink>
          </ul>
        </section>
        <section className="col-sm-12 col-md-8 display2 mb-5 mr-3">
          <div className="pushToLeft pl-3 row">
            <img
              src={Cookbooks_Header}
              className="mainRightSidePicSize "
              alt="This is a pic"
            />

            <div className="size14Font">
              {nutDrivers.map((nutDriver) => (
                <section className="mt-1 mb-3 pl-2">
                  <div
                    className="dotted_line_bottom dotted_line_right content_title boarderTop "
                    style={{ width: "90%" }}
                  >
                    {nutDriver.Name}
                  </div>
                  <div className="row">
                    <div className="col-6 shopDiscriptionSpacer">
                      {nutDriver.Description}
                    </div>
                    <div className="col-6 centerNoFlex">
                      <p>Price: {nutDriver.PriceDescription}</p>
                      <input
                        type="hidden"
                        name="productIdInput"
                        id="productIdInput"
                        value={nutDriver.ProductID}
                      />
                      <p>
                        Quantity:
                        <input
                          onChange={(e) => {
                            handleSetQty(e);
                          }} /* <----- this needs to be checked to see if it contians anything that needs to be replaced */
                          type="number"
                          onKeyDown={(evt) => {
                            if (evt.keyCode == 8) {
                              // I just have to put this check in to kinda break the if else chain so that the backspace can be allowed but if you try to add a number, it wont let you
                            } else if (evt.target.value.length === 2) {
                              evt.preventDefault();
                            } else if (
                              evt.key === "e" ||
                              evt.key === "-" ||
                              evt.key === "+"
                            ) {
                              evt.preventDefault();
                            }
                          }}
                          pattern="([0-9]|[0-9])"
                          maxLength="2"
                          min="1"
                          max="99"
                          style={{ width: "40px" }}
                          name="qtyInput"
                          id="qtyInput"
                          placeholder="0"
                          required
                          key
                        />
                      </p>
                      <p>
                        <input
                          type="image"
                          alt="Add to cart"
                          src={CartButton}
                          onClick={() =>
                            handleAddToCart(
                              qty,
                              nutDriver.ProductID,
                              nutDriver.Name,
                              nutDriver.Price
                            )
                          }
                        />
                      </p>
                      {nutDriver.ImagePath !== "" ? (
                        <img
                          src={`${nutDriver.ImagePath}`}
                          alt="This is a pic"
                          style={{ width: "135px" }}
                        />
                      ) : (
                        ""
                      )}
                    </div>
                  </div>
                </section>
              ))}
            </div>
          </div>
        </section>

        <section className="col-sm-12 col-md-8 display ">
          <div className="pushToLeft row">
            <div className="boardBackgroundShop">
              <img
                src={Cookbooks_Header}
                className="mainRightSidePicSize"
                alt="This is a pic"
                style={{ width: "103%" }}
              ></img>
            </div>
            <div className="paperBackground">
              <div className="size14Font mr-5 pl-4 mt-1">
                {nutDrivers.map((nutDriver) => (
                  <section className="mt-1 mb-3 pl-2">
                    <div
                      className="dotted_line_bottom dotted_line_right content_title boarderTop "
                      style={{ width: "90%" }}
                    >
                      {nutDriver.Name}
                    </div>
                    <div className="row">
                      <div className="col-6 shopDiscriptionSpacer">
                        {nutDriver.Description}
                      </div>
                      <div className="col-6 centerNoFlex">
                        <p>Price: {nutDriver.PriceDescription}</p>
                        <input
                          type="hidden"
                          name="productIdInput"
                          id="productIdInput"
                          value={nutDriver.ProductID}
                        />
                        <p>
                          Quantity:
                          <input
                            onChange={(e) => {
                              handleSetQty(e);
                            }} /* <----- this needs to be checked to see if it contians anything that needs to be replaced */
                            type="number"
                            onKeyDown={(evt) => {
                              if (evt.keyCode == 8) {
                                // I just have to put this check in to kinda break the if else chain so that the backspace can be allowed but if you try to add a number, it wont let you
                              } else if (evt.target.value.length === 2) {
                                evt.preventDefault();
                              } else if (
                                evt.key === "e" ||
                                evt.key === "-" ||
                                evt.key === "+"
                              ) {
                                evt.preventDefault();
                              }
                            }}
                            pattern="([0-9]|[0-9])"
                            maxLength="2"
                            min="1"
                            max="99"
                            style={{ width: "40px" }}
                            name="qtyInput"
                            id="qtyInput"
                            placeholder="0"
                            required
                            key
                          />
                        </p>
                        <p>
                          <input
                            type="image"
                            alt="Add to cart"
                            src={CartButton}
                            onClick={() =>
                              handleAddToCart(
                                qty,
                                nutDriver.ProductID,
                                nutDriver.Name,
                                nutDriver.Price
                              )
                            }
                          />
                        </p>
                        {nutDriver.ImagePath !== "" ? (
                          <img
                            src={`${nutDriver.ImagePath}`}
                            alt="This is a pic"
                          />
                        ) : (
                          ""
                        )}
                      </div>
                    </div>
                  </section>
                ))}
              </div>
            </div>
          </div>

          <div className="paperfooter">
            <div style={{ color: "transparent", height: "30px" }}>.</div>
          </div>
        </section>
      </div>
    </main>
  );
};
export default NutDriverPage;
