import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { ADD_TO_CART, COOKBOOK_LOAD_ITEMS } from "../../redux/actions";
import axios from "axios";
import ShopPageItemComponentMobile from "./ShopPageItemComponentMobile";
import ShopPageItemComponentDesktop from "./ShopPageItemComponentDesktop";
import { NavLink } from "react-router-dom";
import CartButton from "../shared/img/Cart_Button_main.png";
import SpiceRubs_Header from "../shared/img/SpiceRubs_Header.png";
import Shop_MenuHeader from "../shared/img/Shop_MenuHeader.png";

const SpiceRubsPage = () => {
  const url = "http://localhost:5000/api/products/1";
  const [spicerubs, setSpiceRubs] = useState([]);
  const [qty, setQty] = useState(0);
  const dispatch = useDispatch();
  const incomingItems = useSelector((state) => state.itemsReducers);

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios("http://localhost:5000/api/products/1");

      setSpiceRubs(result.data);
    };
    fetchData();
  }, []);

  const handleSetQty = (qty) => {
    setQty(qty.target.value.split(/\D/).join(""));
  };

  const handleAddToCart = (qty, ProductID, Name, Price) => {
    setQty(qty);
    dispatch(ADD_TO_CART(qty, ProductID, Name, Price));
    console.log(qty, ProductID, Name, Price);
    const addToCartAxios = async () => {
      const thisIsTheDataWeArePosting = {
        productIdInput: ProductID,
        qtyInput: qty,
      };
      await axios
        .post("http://localhost:5000/api/cart/add", thisIsTheDataWeArePosting)
        .then(function (response) {
          console.log("thisIsTheDataWeArePutting", response);
        })
        .catch(function (error) {
          console.log(error);
        });
    };
    addToCartAxios();
  };

  return (
    <main className="pl-3 pr-3 fitContent  mt-3">
      <div className="row">
        <div className="dropdown col-12 display2 text-center center">
          <button
            className="btn dropdown-toggle"
            type="button"
            id="dropdownMenuButton"
            data-toggle="dropdown"
            aria-haspopup="true"
            aria-expanded="false"
          >
            <h2>Shop</h2>
          </button>
          <div className="dropdown-menu " aria-labelledby="dropdownMenuButton">
            <NavLink
              className="nav-link-bottom boarderBottom dropdown-item"
              to="/Shop/SpiceRubs"
            >
              SPICE RUBS
            </NavLink>
            <NavLink
              className="nav-link-bottom boarderBottom dropdown-item"
              to="/Shop/Cookbooks"
            >
              COOKBOOKS
            </NavLink>
            <NavLink
              className="nav-link-bottom boarderBottom dropdown-item"
              to="/Shop/BakingPlanks"
            >
              BAKING PLANKS
            </NavLink>
            <NavLink
              className="nav-link-bottom boarderBottom dropdown-item"
              to="/Shop/BBQPlanks"
            >
              BBQ PLANKS
            </NavLink>
            <NavLink
              className="nav-link-bottom boarderBottom dropdown-item"
              to="/Shop/NutDriver "
            >
              NUT DRIVER
            </NavLink>
          </div>
        </div>

        <section className="col-4 display size14Font center">
          <ul className="hideUlDot ">
            <img src={Shop_MenuHeader} alt="This is a pic" />
            <NavLink
              className="nav-link-bottom boarderBottom"
              to="/Shop/SpiceRubs"
            >
              SPICE RUBS
            </NavLink>

            <NavLink
