import { Promise } from "mssql";
import axios from "axios";
const initialState = [];

const cartReducers = (state = initialState, action) => {
  let array = [];
  switch (action.type) {
    case "ADD_TO_CART":
      state.push(action.payload);
      return state;
    case "CART_LOAD_PAGE":
      state = action.payload;
      return state;
