        const result = await axios("http://localhost:5000/api/cart/list");
        console.log("line 14 cartReducers: ", result.data);
        if (result.data.length > 0) {
          result.data.map((item) => {
            state.push(item);
          });
          return state;
        } else {
          return state;
        }
      };
      fetchData(); */
    case "CART_UPDATE_PAGE":
      console.log((state = action.payload.orderItemId));
      let cloneOfState = state;

      let indexOfItem = array.indexOf(action.payload.orderItemId);
      if (indexOfItem > 0) {
        array.splice(index, 1);
        state = cloneOfState;
      }
      //Find item based on its key
      return state;
    default:
      return state;
  }
};

export default cartReducers;
