import axios from "axios";

export const GET_SESSION_ID = () => {
  return {
    type: "GET_SESSION_ID",
  };
};
export const ADD_TO_CART = (qty = 1, ProductID = 2, Name = "", Price = 0) => {
  return {
    type: "ADD_TO_CART",
    payload: { qty, ProductID, Name, Price },
  };
};

export const CART_UPDATE_PAGE = (qty = 1, orderItemId = 1) => {
  return {
    type: "CART_UPDATE_PAGE",
    payload: { qty, orderItemId },
  };
};

export const CART_DELETE_ITEM = (orderItemId = 1) => {
  return {
    type: "CART_DELETE_ITEM",
    payload: { orderItemId },
  };
};
export const STEPTHREE_UPDATE_PAGE = (orderItemId = 1) => {
  return {
    type: "STEPTHREE_UPDATE_PAGE",
    payload: { orderItemId },
  };
};

export const CHECKOUT_UPDATE_PAGE = (
  billingFirstName = "",
  billingLastName = "",
  billingAddress1 = "",
  billingAddress2 = "",
  billingCity = "",
  billingState = "",
  billingPostalCode = "",
  billingPhone = "",
  billingEmail = "",

  shippingFirstName = "",
  shippingLastName = "",
  shippingAddress1 = "",
  shippingAddress2 = "",
  shippingCity = "",
  shippingState = "",
  shippingPostalCode = "",
  shippingPhone = "",
  shippingEmail = "",
  note = ""
) => {
  return {
    type: "CHECKOUT_UPDATE_PAGE",
    payload: {
      billingFirstName,
      billingLastName,
      billingAddress1,
      billingAddress2,
      billingCity,
      billingState,
      billingPostalCode,
      billingPhone,
      billingEmail,

      shippingFirstName,
      shippingLastName,
      shippingAddress1,
      shippingAddress2,
      shippingCity,
      shippingState,
      shippingPostalCode,
      shippingPhone,
      shippingEmail,

      note,
    },
  };
};

export const COOKBOOK_LOAD_ITEMS = () => {
  return {
    type: "COOKBOOK_LOAD_ITEMS",
  };
};

export const CART_LOAD_PAGE = (Cart = []) => {
  return {
    type: "CART_LOAD_PAGE",
    payload: Cart,
  };
};
export const CART_EDIT_PAGE = (qtyInput = 0, orderItemIdInput = 1) => {
  return {
    type: "CART_EDIT_PAGE",
    payload: { qtyInput, orderItemIdInput },
  };
};
