import { Promise } from "mssql";
import axios from "axios";
const getItemsInCart = async () => {
  const itemsInCart = axios.get("http://localhost:5000/api/cart/list");
  const a = await itemsInCart;
  console.log("This is line 11 in the cartReducers page: ", a.data);
  return a.data;
};
const initialState = getItemsInCart();

const cartReducers = (state = initialState, action) => {
  switch (action.type) {
    case "ADD_TO_CART":
      let array = state;
      array.push(action.payload);
      return state;
    case "CART_LOAD_PAGE":
      initialState.push(getItemsInCart());
      console.log("This is the state after the array2 push: ", state);
      return state;
    default:
      return state;
  }
};

export default cartReducers;
