const initialState = [];

const cartReducers = (state = initialState, action) => {
  switch (action.type) {
    case "ADD_TO_CART":
      let array = state;
      array.push(action.payload);
      return state; //Take qty and product id and activate a function that pushes it to the cart reducer
    case "CART_LOAD_PAGE":
      //For every item api List brings back, push it into an array for the state to render
      let cart = [];
      cart.push(action.payload);
      return state;
    default:
      return state;
  }
};

export default cartReducers;
