const initialState = [];

const cartReducers = (state = initialState, action) => {
  switch (action.type) {
    case "ADD_TO_CART":
      state.push(action.payload);
      return state;
    case "CART_LOAD_PAGE":
      state = action.payload;
      return state;
    case "CART_DELETE_ITEM":
      const index = state.indexOf(action.payload);
      state.splice(index, 1);
      return state;
    default:
      return state;
  }
};

export default cartReducers;
