import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { ADD_TO_CART, COOKBOOK_LOAD_ITEMS } from "../../redux/actions";
import axios from "axios";
import ShopPageItemComponentMobile from "./ShopPageItemComponentMobile";
import ShopPageItemComponentDesktop from "./ShopPageItemComponentDesktop";
import { NavLink } from "react-router-dom";
import CartButton from "../shared/img/Cart_Button_main.png";
import Cookbooks_Header from "../shared/img/Cookbooks_Header.png";
import Shop_MenuHeader from "../shared/img/Shop_MenuHeader.png";
const CookBooksPage = () => {
  const [cookbooks, setCookBooks] = useState([]);
  const [qty, setQty] = useState(0);
  const dispatch = useDispatch();
  const incomingItems = useSelector((state) => state.itemsReducers);

  useEffect(() => {
    dispatch(COOKBOOK_LOAD_ITEMS());
    setCookBooks(incomingItems);
  }, []);

  const handleSetQty = (qty) => {
    setQty(qty.target.value.split(/\D/).join(""));
  };

  const handleAddToCart = (qty, ProductID, Name, Price) => {
    setQty(qty);
    dispatch(ADD_TO_CART(qty, ProductID, Name, Price));
    console.log(qty, ProductID, Name, Price);
    const addToCartAxios = async () => {
      const thisIsTheDataWeArePosting = {
        productIdInput: ProductID,
        qtyInput: qty,
      };
      await axios
        .post("http://localhost:5000/api/cart/add", thisIsTheDataWeArePosting)
        .then(function (response) {
          console.log("thisIsTheDataWeArePutting", response);
        })
        .catch(function (error) {
          console.log(error);
        });
    };
    addToCartAxios();
  };

  return (
    <main className="pl-3 pr-3 fitContent  mt-3">
      <div className="row">
        <div className="dropdown col-12 display2 text-center center">
          <button
            className="btn dropdown-toggle"
            type="button"
            id="dropdownMenuButton"
            data-toggle="dropdown"
            aria-haspopup="true"
            aria-expanded="false"
          >
            <h2>Shop</h2>
          </button>
          <div className="dropdown-menu " aria-labelledby="dropdownMenuButton">
            <NavLink
              className="nav-link-bottom boarderBottom dropdown-item"
              to="/Shop/SpiceRubs"
            >
              SPICE RUBS
            </NavLink>
            <NavLink
              className="nav-link-bottom boarderBottom dropdown-item"
              to="/Shop/Cookbooks"
            >
              COOKBOOKS
            </NavLink>
            <NavLink
              className="nav-link-bottom boarderBottom dropdown-item"
              to="/Shop/BakingPlanks"
            >
              BAKING PLANKS
            </NavLink>
            <NavLink
              className="nav-link-bottom boarderBottom dropdown-item"
              to="/Shop/BBQPlanks"
            >
              BBQ PLANKS
            </NavLink>
            <NavLink
              className="nav-link-bottom boarderBottom dropdown-item"
              to="/Shop/NutDriver "
            >
              NUT DRIVER
            </NavLink>
          </div>
        </div>

        <section className="col-4 display size14Font center">
          <ul className="hideUlDot ">
            <img src={Shop_MenuHeader} alt="This is a pic" />
            <NavLink
              className="nav-link-bottom boarderBottom"
              to="/Shop/SpiceRubs"
            >
              SPICE RUBS
            </NavLink>

            <NavLink
              className="nav-link-bottom boarderBottom"
              to="/Shop/Cookbooks"
            >
              COOKBOOKS
            </NavLink>

            <NavLink
              className="nav-link-bottom boarderBottom"
              to="/Shop/BakingPlanks"
            >
              BAKING PLANKS
            </NavLink>
            <NavLink
              className="nav-link-bottom boarderBottom"
              to="/Shop/BBQPlanks"
            >
              BBQ PLANKS
            </NavLink>
            <NavLink
              className="nav-link-bottom boarderBottom"
              to="/Shop/NutDriver"
            >
              NUT DRIVERS
            </NavLink>
          </ul>
        </section>
        <section className="col-sm-12 col-md-8 display2 mb-5 mr-3">
          <div className="pushToLeft pl-3 row">
            <img
              src={Cookbooks_Header}
              className="mainRightSidePicSize "
              alt="This is a pic"
            />

            <div className="size14Font">
              {cookbooks.map((cookbook) => (
                <ShopPageItemComponentMobile
                  cookbook={cookbook}
                  CartButton={CartButton}
                  handleAddToCart={handleAddToCart}
                  handleSetQty={handleSetQty}
                  qty={qty}
                  dispatch={dispatch}
                  ADD_TO_CART={ADD_TO_CART}
                />
              ))}
            </div>
          </div>
        </section>

        <section className="col-sm-12 col-md-8 display ">
          <div className="pushToLeft row">
            <div className="boardBackgroundShop">
              <img
                src={Cookbooks_Header}
                className="mainRightSidePicSize"
                alt="This is a pic"
                style={{ width: "103%" }}
              ></img>
            </div>
            <div className="paperBackground">
              <div className="size14Font mr-5 pl-4 mt-1">
                {cookbooks.map((cookbook) => (
                  <ShopPageItemComponentDesktop
                    cookbook={cookbook}
                    CartButton={CartButton}
                    handleAddToCart={handleAddToCart}
                    handleSetQty={handleSetQty}
                    qty={qty}
                    dispatch={dispatch}
                    ADD_TO_CART={ADD_TO_CART}
                  />
                ))}
              </div>
            </div>
          </div>

          <div className="paperfooter">
            <div style={{ color: "transparent", height: "30px" }}>.</div>
          </div>
        </section>
      </div>
    </main>
  );
};
export default CookBooksPage;
