import axios from "axios";

export const ADD_TO_CART = (qty = 1, ProductID = 2, Name = "", Price = 0) => {
  return {
    type: "ADD_TO_CART",
    payload: { qty, ProductID, Name, Price },
  };
};

export const COOKBOOK_LOAD_ITEMS = () => {
  return {
    type: "COOKBOOK_LOAD_ITEMS",
  };
};

export const CART_LOAD_PAGE = (Cart = []) => {
  return {
    type: "CART_LOAD_PAGE",
    payload: Cart,
  };
};
export const CART_EDIT_PAGE = (qtyInput = 0, orderItemIdInput = 1) => {
  return {
    type: "CART_EDIT_PAGE",
    payload: { qtyInput, orderItemIdInput },
  };
};
