import React, { useState, useEffect } from "react";
import { NavLink, useHistory } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { STEPTHREE_UPDATE_PAGE } from "../../redux/actions";
import Checkout from "../shared/img/Checkout_button.png";
import Purchase from "../shared/img/Purchase_button.png";
import Back from "../shared/img/Back_button.png";
const StepThreePage = () => {
  const history = useHistory();
  const dispatch = useDispatch();
  const cartItems = useSelector((state) => state.cartReducers);
  const cartOrderState = useSelector((state) => state.orderCartReducers);
  const pageThreeState = useSelector((state) => state.pageThreeReducers);

  const [shippingCost, setShippingCost] = useState(pageThreeState.shippingCost);
  const [cardType, setCardType] = useState(pageThreeState.cardType);
  // The total is not going to be shown until the next page, this page will rely on frontend calculations
  let subtotal = 0;
  const tax = 5.0;
  let total = 0;

  const formatter = new Intl.NumberFormat("en-US", {
    minimumFractionDigits: 2,
    maximumFractionDigits: 2,
  });

  const updateShipping = (shippingCost) => {
    if (shippingCost.target.value == 0) {
      setShippingCost(0);
    } else {
      setShippingCost(shippingCost.target.value);
    }
  };
  const updateCardType = (cardType) => {
    if (cardType.target.value == "Select") {
      setCardType("Select");
    } else {
      setCardType(cardType.target.value);
    }
  };
  const purchaseClicked = () => {
    if (
      cardType != "Select" &&
      cardType != "" &&
      cardType != "Default" &&
      shippingCost != 0
    ) {
      console.log(shippingCost, total, cardType);
      dispatch(STEPTHREE_UPDATE_PAGE(Number(shippingCost), cardType));
      history.push("/cart/stepfour");
    }
  };
  return (
    <main className="mt-4">
      <section className="center display ">
        <div className="row container brownCartBackground  cartText">
          <div className="col-12 center">
            <p>Preview Order & Enter Payment Information Step 3 of 4</p>
          </div>
        </div>

        <div
          className="row container  lightbrownCartBackground  cartText "
          /* style="width:1140px" */
        >
          <div className="col-6">
            <div className=" brownCartBackground">
              <p>Billing Address</p>
            </div>
          </div>
          <div className="col-6">
            <div className=" brownCartBackground">
              <p>Shipping Address</p>
            </div>
          </div>

          <div className="col-6">
            <table
              cellpadding="5"
              cellspacing="5"
              width="100%"
              className="formBorder"
            >
              <tbody>
                <tr className="mb-5 lightbrownCartBackground">
                  <td className="pushToRight formPadding">Name:</td>
                  <td className="formPadding center">
                    {cartOrderState.billingFirstName}{" "}
                    {cartOrderState.billingLastName}
                  </td>
                </tr>
                <tr className="lightbrownCartBackground">
                  <td className="pushToRight formPadding">Address:</td>
                  <td className="formPadding center">
                    <p className="stepThreeSpacer">
                      {cartOrderState.billingAddress1}{" "}
                      {cartOrderState.billingAddress2},{" "}
                      {cartOrderState.billingCity},{" "}
                      {cartOrderState.billingState},{" "}
                      {cartOrderState.billingPostalCode}
                    </p>
                  </td>
                </tr>

                <tr className="lightbrownCartBackground">
                  <td className="pushToRight formPadding">Phone:</td>
                  <td className="formPadding center">
                    {cartOrderState.billingPhone}
                  </td>
                </tr>
                <tr className="lightbrownCartBackground">
                  <td className="pushToRight formPadding">Email:</td>
                  <td className="formPadding center">
                    {cartOrderState.billingEmail}
                  </td>
                </tr>
              </tbody>
            </table>
          </div>
          <div className="col-6">
            <table
              cellpadding="5"
              cellspacing="5"
              width="100%"
              className="formBorder"
            >
              <tbody>
                <tr className="mb-5 lightbrownCartBackground">
                  <td className="pushToRight formPadding">Name:</td>
                  <td className="formPadding center">
                    {cartOrderState.shippingFirstName}
                  </td>
                </tr>
                <tr className="lightbrownCartBackground">
                  <td className="pushToRight formPadding">Address:</td>
                  <td className="formPadding center">
                    <p className="stepThreeSpacer">
                      {cartOrderState.shippingAddress1}{" "}
                      {cartOrderState.shippingAddress2},{" "}
                      {cartOrderState.shippingCity},{" "}
                      {cartOrderState.shippingState},{" "}
                      {cartOrderState.shippingPostalCode}
                    </p>
                  </td>
                </tr>

                <tr className="lightbrownCartBackground">
                  <td className="pushToRight formPadding">Phone:</td>
                  <td className="formPadding center">
                    {cartOrderState.shippingPhone}
                  </td>
                </tr>
                <tr className="lightbrownCartBackground">
                  <td className="pushToRight formPadding">Email:</td>
                  <td className="formPadding center">
                    {cartOrderState.shippingEmail}
                  </td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>

        <div className="row container lightbrownCartBackground cartText mt-4 ">
          <table border="0" cellpadding="0" cellspacing="0" width="100%">
            <tbody>
              <tr>
                <td
                  style={{
                    width: "500px",
                    height: "25px",
                    padding: "5px",
                    backgroundColor: "#d2be9d",
                    color: "#4b290c",
                    fontWeight: "bold",
                    borderTop: "1px dashed #929292",
                    borderLeft: "1px dashed #929292",
                  }}
                >
                  Shipping to You
                </td>
                <td
                  style={{
                    width: "500px",
                    height: "25px",
                    padding: "5px",
                    backgroundColor: "#d2be9d",
                    color: "#4b290c",
                    fontWeight: "bold",
                    borderTop: "1px dashed #929292",
                    borderLeft: "1px dashed #929292",
                  }}
                >
                  Price
                </td>
                <td
                  style={{
                    width: "500px",
                    height: "25px",
                    padding: "5px",
                    backgroundColor: "#d2be9d",
                    color: "#4b290c",
                    fontWeight: "bold",
                    borderTop: "1px dashed #929292",
                    borderLeft: "1px dashed #929292",
                  }}
                >
                  Quantity
                </td>
                <td
                  style={{
                    width: "100px",
                    height: "25px",
                    textAlign: "center",
                    backgroundColor: "#d2be9d",
                    color: "#4b290c",
                    fontWeight: "bold",
                    borderTop: "1px dashed #929292",
                    borderLeft: "1px dashed #929292",
                    borderRight: "1px dashed #929292",
                  }}
                >
                  Total
                </td>
              </tr>
              {cartItems.map((item) => (
                <tr
                  ng-repeat="product in ctrl.shopSvc.orderCart.products"
                  className="ng-scope"
                >
                  <td
                    style={{
                      padding: "5px",
                      color: "#4b290c",
                      borderTop: "1px dashed #929292",
                      borderLeft: "1px dashed #929292",
                      textAlign: "center",
                    }}
                    className="ng-binding"
                  >
                    {item.name}
                  </td>
                  <td
                    style={{
                      padding: "5px",
                      color: "#4b290c",
                      borderTop: "1px dashed #929292",
                      borderLeft: "1px dashed #929292",
                      textAlign: "center",
                    }}
                    className="ng-binding"
                  >
                    ${formatter.format(item.price)}
                  </td>
                  <td
                    style={{
                      padding: "5px",
                      color: "#4b290c",
                      borderTop: "1px dashed #929292",
                      borderLeft: "1px dashed #929292",
                      textAlign: "center",
                    }}
                  >
                    {item.qty}
                  </td>

                  <td
                    style={{
                      borderTop: "1px dashed #929292",
                      borderLeft: "1px dashed #929292",
                      color: "#4b290c",
                      border: "1px solid #929292; text-align:center",
                      textAlign: "center",
                      borderRight: "1px dashed #929292",
                    }}
                  >
                    <div
                      style={{
                        paddingTop: "10px",
                      }}
                      className="ng-binding"
                    >
                      ${formatter.format(item.total)}
                      <div style={{ visibility: "hidden" }}>
                        {(subtotal += item.qty * item.price)}
                      </div>
                    </div>
                  </td>
                </tr>
              ))}

              <tr
                style={{
                  height: "35px",
                  color: "#4b290c",
                  fontWeight: "bold",
                  textAlign: "right",
                  borderTop: "1px dashed #929292",
                }}
              >
                <td
                  style={{
                    textAlign: "right",
                    color: "#4b290c",
                    fontWeight: "bold",
                  }}
                ></td>
                <td
                  style={{
                    paddingTop: "5px",
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "center",
                  }}
                >
                  &nbsp;
                </td>
                <td
                  style={{
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "right",
                  }}
                >
                  Subtotal:
                </td>
                <td
                  style={{
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "center",
                  }}
                  className="ng-binding"
                >
                  ${formatter.format(subtotal)}
                </td>
              </tr>
              <tr
                style={{
                  height: "35px",
                  color: "#4b290c",
                  fontWeight: "bold",
                  textAlign: "right",
                }}
              >
                <td
                  style={{
                    textAlign: "right",
                    color: "#4b290c",
                    fontWeight: "bold",
                  }}
                ></td>
                <td
                  style={{
                    paddingTop: "5px",
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "center",
                  }}
                >
                  &nbsp;
                </td>
                <td
                  style={{
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "right",
                  }}
                >
                  Tax:
                </td>
                <td
                  style={{
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "center",
                  }}
                  className="ng-binding"
                >
                  $5.00
                </td>
              </tr>
              <tr
                style={{
                  height: "35px",
                  color: "#4b290c",
                  fontWeight: "bold",
                  textAlign: "right",
                }}
              >
                <td
                  style={{
                    textAlign: "right",
                    color: "#4b290c",
                    fontWeight: "bold",
                  }}
                ></td>
                <td
                  style={{
                    paddingTop: "5px",
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "center",
                  }}
                >
                  &nbsp;
                </td>
                <td
                  style={{
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "right",
                  }}
                >
                  *Shipping and handling{" "}
                  <select
                    style={{ width: "200px" }}
                    name="shippingInput"
                    value={shippingCost}
                    id="shippingInput"
                    required="required"
                    onChange={(e) => {
                      updateShipping(e);
                    }}
                  >
                    <option selected value="0">
                      Select
                    </option>
                    <option value="5">U.S.P.S Parcel (3 - 8 days)</option>
                    <option value="10">U.S.P.S Priority (2 - 4 days)</option>
                  </select>
                  :
                </td>
                <td
                  style={{
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "center",
                  }}
                  className="ng-binding"
                >
                  ${formatter.format(shippingCost)}
                </td>
              </tr>
              <tr
                style={{
                  height: "35px",
                  color: "#4b290c",
                  fontWeight: "bold",
                  textAlign: "right",
                }}
              >
                <td
                  style={{
                    textAlign: "right",
                    color: "#4b290c",
                    fontWeight: "bold",
                  }}
                ></td>
                <td
                  style={{
                    paddingTop: "5px",
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "center",
                  }}
                >
                  &nbsp;
                </td>
                <td
                  style={{
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "right",
                  }}
                >
                  Total:
                </td>
                <td
                  style={{
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "center",
                  }}
                  className="ng-binding"
                >
                  ${formatter.format(subtotal + tax + Number(shippingCost))}
                </td>
              </tr>
            </tbody>
          </table>
        </div>
        <div className="row container lightbrownCartBackground   mt-3 cartText">
          <div className="col-12 center brownCartBackground">
            <p>Extra Comments</p>
          </div>
          <div className="col-12 center lightbrownCartBackground">
            <p>{cartOrderState.note}</p>
          </div>
        </div>

        <div className="row container lightbrownCartBackground   mt-3 cartText">
          <div className="col-12 center brownCartBackground">
            <p>Payment Information</p>
          </div>
          <div className="col-12 pushToLeft lightbrownCartBackground">
            <p>
              Card Type:
              <select
                onChange={(e) => {
                  updateCardType(e);
                }}
                value={cardType}
                name="cardTypeInput"
                id="cardTypeInput"
              >
                <option value="Default">Select</option>
                <option value="Visa">Visa</option>
                <option value="Master">MasterCard</option>
                <option value="Discover">Discover</option>
                <option value="American">American Express</option>
              </select>
            </p>
          </div>
          <div className="col-12 pushToLeft lightbrownCartBackground">
            <p>
              <label for="cardNumber">Card Number:</label>
              <input placeholder="Card Number" />
              Expiration:
              <select name="experationInput" id="experationInput">
                <option value="default">Select</option>
                <option value="january">January</option>
                <option value="february">February</option>
                <option value="march">March</option>
                <option value="april">April</option>
                <option value="may">May</option>
                <option value="june">June</option>
                <option value="july">July</option>
                <option value="august">August</option>
                <option value="september">September</option>
                <option value="october">October</option>
                <option value="november">November</option>
                <option value="december">December</option>
              </select>
              <select>
                <option value="default">Select</option>
                <option value="2020">2020</option>
                <option value="2021">2021</option>
                <option value="2022">2022</option>
                <option value="2023">2023</option>
                <option value="2024">2024</option>
                <option value="2025">2025</option>
                <option value="2026">2026</option>
                <option value="2027">2027</option>
                <option value="2028">2028</option>
                <option value="2029">2029</option>
                <option value="2030">2030</option>
              </select>
            </p>
          </div>
        </div>

        <div className="row container lightbrownCartBackground   mt-3 cartText">
          <div className="col-12 pushToRight lightbrownCartBackground">
            <NavLink to="/cart/checkout" className="pr-3">
              <img src={Back} alt=""></img>
            </NavLink>

            <img
              src={Checkout}
              type="button"
              className="pr-3"
              onClick={(e) => purchaseClicked(e)}
            />
          </div>
        </div>
      </section>
      {/* ------------------------------------------------------------------------------------------------------------------*/}
      <section className="display2 center">
        <div
          className="col-12 lightbrownCartBackground mt-3"
          style={{ color: "black" }}
        >
          <div className=" brownCartBackground  cartText">
            <p>Payment Information Step 3 of 4</p>
          </div>

          <div className="col-12">
            <div className="center brownCartBackground">
              <p>Billing Address</p>
            </div>
          </div>

          <div className="col-12">
            <table
              cellpadding="5"
              cellspacing="5"
              width="100%"
              className="formBorder"
            >
              <tbody>
                <tr className="mb-5 lightbrownCartBackground">
                  <td className="pushToRight formPadding">Name:</td>
                  <td className="formPadding center">
                    {cartOrderState.billingFirstName}{" "}
                    {cartOrderState.billingLastName}
                  </td>
                </tr>
                <tr className="lightbrownCartBackground">
                  <td className="pushToRight formPadding">Address:</td>
                  <td className="formPadding center">
                    <p className="stepThreeSpacer">
                      {cartOrderState.billingAddress1}{" "}
                      {cartOrderState.billingAddress2} ,{" "}
                      {cartOrderState.billingCity},{" "}
                      {cartOrderState.billingFirstState},{" "}
                      {cartOrderState.billingPostalCode}
                    </p>
                  </td>
                </tr>

                <tr className="lightbrownCartBackground">
                  <td className="pushToRight formPadding">Phone:</td>
                  <td className="formPadding center">
                    {" "}
                    {cartOrderState.billingPhone}
                  </td>
                </tr>
                <tr className="lightbrownCartBackground">
                  <td className="pushToRight formPadding">Email:</td>
                  <td className="formPadding center">
                    {" "}
                    {cartOrderState.billingEmail}
                  </td>
                </tr>
              </tbody>
            </table>
          </div>
          <div className="col-12">
            <div className="center brownCartBackground">
              <p>Shipping Address</p>
            </div>
          </div>
          <div className="col-12">
            <table
              cellpadding="5"
              cellspacing="5"
              width="100%"
              className="formBorder"
            >
              <tbody>
                <tr className="mb-5 lightbrownCartBackground">
                  <td className="pushToRight formPadding">Name:</td>
                  <td className="formPadding center">
                    {cartOrderState.shippingFirstName}{" "}
                    {cartOrderState.shippingLastName}
                  </td>
                </tr>
                <tr className="lightbrownCartBackground">
                  <td className="pushToRight formPadding">Address:</td>
                  <td className="formPadding center">
                    <p className="stepThreeSpacer">
                      {cartOrderState.shippingAddress1}
                      {cartOrderState.shippingAddress2}{" "}
                      {cartOrderState.shippingState} ,
                      {cartOrderState.shippingCity} ,{" "}
                      {cartOrderState.shippingState}
                    </p>
                  </td>
                </tr>

                <tr className="lightbrownCartBackground">
                  <td className="pushToRight formPadding">Phone:</td>
                  <td className="formPadding center">
                    {cartOrderState.shippingPhone}
                  </td>
                </tr>
                <tr className="lightbrownCartBackground">
                  <td className="pushToRight formPadding">Email:</td>
                  <td className="formPadding center">
                    {cartOrderState.shippingEmail}
                  </td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>

        <div className="col-12 lightbrownCartBackground cartText mt-4">
          <table border="0" cellpadding="0" cellspacing="0" width="100%">
            <tbody>
              <tr>
                <td
                  style={{
                    width: "500px",
                    height: "25px",
                    padding: "5px",
                    backgroundColor: "#d2be9d",
                    color: "#4b290c",
                    fontWeight: "bold",
                    borderTop: "1px dashed #929292",
                    borderLeft: "1px dashed #929292",
                  }}
                >
                  Shipping to You
                </td>
                <td
                  style={{
                    width: "500px",
                    height: "25px",
                    padding: "5px",
                    backgroundColor: "#d2be9d",
                    color: "#4b290c",
                    fontWeight: "bold",
                    borderTop: "1px dashed #929292",
                    borderLeft: "1px dashed #929292",
                  }}
                >
                  Price
                </td>
                <td
                  style={{
                    width: "500px",
                    height: "25px",
                    padding: "5px",
                    backgroundColor: "#d2be9d",
                    color: "#4b290c",
                    fontWeight: "bold",
                    borderTop: "1px dashed #929292",
                    borderLeft: "1px dashed #929292",
                  }}
                >
                  Quantity
                </td>
                <td
                  style={{
                    width: "100px",
                    height: "25px",
                    textAlign: "center",
                    backgroundColor: "#d2be9d",
                    color: "#4b290c",
                    fontWeight: "bold",
                    borderTop: "1px dashed #929292",
                    borderLeft: "1px dashed #929292",
                    borderRight: "1px dashed #929292",
                  }}
                >
                  Total
                </td>
              </tr>
              {cartItems.map((item) => (
                <tr
                  ng-repeat="product in ctrl.shopSvc.orderCart.products"
                  className="ng-scope"
                >
                  <td
                    style={{
                      padding: "5px",
                      color: "#4b290c",
                      borderTop: "1px dashed #929292",
                      borderLeft: "1px dashed #929292",
                      textAlign: "center",
                    }}
                    className="ng-binding"
                  >
                    {item.name}
                  </td>
                  <td
                    style={{
                      padding: "5px",
                      color: "#4b290c",
                      borderTop: "1px dashed #929292",
                      borderLeft: "1px dashed #929292",
                      textAlign: "center",
                    }}
                    className="ng-binding"
                  >
                    ${formatter.format(item.price)}
                  </td>
                  <td
                    style={{
                      padding: "5px",
                      color: "#4b290c",
                      borderTop: "1px dashed #929292",
                      borderLeft: "1px dashed #929292",
                      textAlign: "center",
                    }}
                  >
                    {item.qty}
                  </td>

                  <td
                    style={{
                      borderTop: "1px dashed #929292",
                      borderLeft: "1px dashed #929292",
                      color: "#4b290c",
                      border: "1px solid #929292; text-align:center",
                      textAlign: "center",
                      borderRight: "1px dashed #929292",
                    }}
                  >
                    <div
                      style={{
                        paddingTop: "10px",
                      }}
                      className="ng-binding"
                    >
                      ${formatter.format(item.total)}
                      <div style={{ visibility: "hidden" }}>
                        {(subtotal += item.qty * item.price)}
                      </div>
                    </div>
                  </td>
                </tr>
              ))}

              <tr
                style={{
                  height: "35px",
                  color: "#4b290c",
                  fontWeight: "bold",
                  textAlign: "right",
                  borderTop: "1px dashed #929292",
                }}
              >
                <td
                  style={{
                    textAlign: "right",
                    color: "#4b290c",
                    fontWeight: "bold",
                  }}
                ></td>
                <td
                  style={{
                    paddingTop: "5px",
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "center",
                  }}
                >
                  &nbsp;
                </td>
                <td
                  style={{
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "right",
                  }}
                >
                  Subtotal:
                </td>
                <td
                  style={{
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "center",
                  }}
                  className="ng-binding"
                >
                  ${formatter.format(subtotal)}
                </td>
              </tr>
              <tr
                style={{
                  height: "35px",
                  color: "#4b290c",
                  fontWeight: "bold",
                  textAlign: "right",
                  borderTop: "1px dashed #929292",
                }}
              >
                <td
                  style={{
                    textAlign: "right",
                    color: "#4b290c",
                    fontWeight: "bold",
                  }}
                ></td>
                <td
                  style={{
                    paddingTop: "5px",
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "center",
                  }}
                >
                  &nbsp;
                </td>
                <td
                  style={{
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "right",
                  }}
                >
                  Tax:
                </td>
                <td
                  style={{
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "center",
                  }}
                  className="ng-binding"
                >
                  $5.00
                </td>
              </tr>
              <tr
                style={{
                  height: "35px",
                  color: "#4b290c",
                  fontWeight: "bold",
                  textAlign: "right",
                  borderTop: "1px dashed #929292",
                }}
              >
                <td
                  style={{
                    textAlign: "right",
                    color: "#4b290c",
                    fontWeight: "bold",
                  }}
                ></td>
                <td
                  style={{
                    paddingTop: "5px",
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "center",
                  }}
                >
                  &nbsp;
                </td>
                <td
                  style={{
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "right",
                  }}
                >
                  *Shipping and handling{" "}
                  <select
                    style={{ width: "200px" }}
                    name="shippingInput"
                    value={shippingCost}
                    id="shippingInput"
                    required="required"
                    onChange={(e) => {
                      updateShipping(e);
                    }}
                  >
                    <option selected value="0">
                      Select
                    </option>
                    <option value="5">U.S.P.S Parcel (3 - 8 days)</option>
                    <option value="10">U.S.P.S Priority (2 - 4 days)</option>
                  </select>
                  :
                </td>
                <td
                  style={{
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "center",
                  }}
                  className="ng-binding"
                >
                  ${formatter.format(shippingCost)}
                </td>
              </tr>
              <tr
                style={{
                  height: "35px",
                  color: "#4b290c",
                  fontWeight: "bold",
                  textAlign: "right",
                  borderTop: "1px dashed #929292",
                }}
              >
                <td
                  style={{
                    textAlign: "right",
                    color: "#4b290c",
                    fontWeight: "bold",
                  }}
                ></td>
                <td
                  style={{
                    paddingTop: "5px",
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "center",
                  }}
                >
                  &nbsp;
                </td>
                <td
                  style={{
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "right",
                  }}
                >
                  Total:
                </td>
                <td
                  style={{
                    height: "35px",
                    color: "#4b290c",
                    fontWeight: "bold",
                    textAlign: "center",
                  }}
                  className="ng-binding"
                >
                  ${formatter.format(subtotal + tax + Number(shippingCost))}
                </td>
              </tr>
            </tbody>
          </table>
        </div>
        <div className="col-12 lightbrownCartBackground   mt-3 cartText">
          <div className=" center brownCartBackground">
            <p>Extra Comments</p>
          </div>
          <div className="col-12 center lightbrownCartBackground">
            <p>Note gos here</p>
          </div>
        </div>

        <div className="col-12 lightbrownCartBackground   mt-3 cartText">
          <div className="col-12 center brownCartBackground">
            <p>Payment Information</p>
          </div>
          <div className="col-12 pushToLeft lightbrownCartBackground">
            <p>
              Card Type:
              <form action="/cart/stepthree/updatecard" method="POST">
                <select
                  name="cardTypeInput"
                  id="cardTypeInput"
                  onChange={(e) => {
                    updateCardType(e);
                  }}
                  value={cardType}
                >
                  <option value="Default">Select</option>
                  <option value="Visa">Visa</option>
                  <option value="Master">MasterCard</option>
                  <option value="Discover">Discover</option>
                  <option value="American">American Express</option>
                </select>
              </form>
            </p>
          </div>
          <div className="col-12 pushToLeft lightbrownCartBackground">
            <p>
              <label for="cardNumber">Card Number:</label>
              <input placeholder="Card Number" />
              Expiration:
              <select name="experationInput" id="experationInput">
                <option value="default">Select</option>
                <option value="january">January</option>
                <option value="february">February</option>
                <option value="march">March</option>
                <option value="april">April</option>
                <option value="may">May</option>
                <option value="june">June</option>
                <option value="july">July</option>
                <option value="august">August</option>
                <option value="september">September</option>
                <option value="october">October</option>
                <option value="november">November</option>
                <option value="december">December</option>
              </select>
              <select>
                <option value="default">Select</option>
                <option value="2020">2020</option>
                <option value="2021">2021</option>
                <option value="2022">2022</option>
                <option value="2023">2023</option>
                <option value="2024">2024</option>
                <option value="2025">2025</option>
                <option value="2026">2026</option>
                <option value="2027">2027</option>
                <option value="2028">2028</option>
                <option value="2029">2029</option>
                <option value="2030">2030</option>
              </select>
            </p>
          </div>
        </div>

        <div className="col-12 lightbrownCartBackground   mt-3 cartText">
          <div className="col-12 pushToRight lightbrownCartBackground">
            <NavLink to="/cart/checkout" className="pr-3">
              <img src={Back} alt=""></img>
            </NavLink>

            <img
              src={Checkout}
              type="button"
              className="pr-3"
              onClick={(e) => purchaseClicked(e)}
            />
          </div>
        </div>
      </section>
    </main>
  );
};
export default StepThreePage;
