import React, { useState, useEffect } from "react";
import axios from "axios";
import { useSelector, useDispatch } from "react-redux";
import { ADD_TO_CART, COOKBOOK_LOAD_ITEMS } from "../../redux/actions";

import ShopPageItemComponentMobile from "./ShopPageItemComponentMobile";
import ShopPageItemComponentDesktop from "./ShopPageItemComponentDesktop";
import { NavLink } from "react-router-dom";
import CartButton from "../shared/img/Cart_Button_main.png";
import Cookbook_Header from "../shared/img/Cookbooks_Header.png";
import Shop_MenuHeader from "../shared/img/Shop_MenuHeader.png";

const NutDriverPage = () => {
  const [nutDrivers, setNutDriver] = useState({});
  const [qty, setQty] = useState(0);
  const dispatch = useDispatch();

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios("http://localhost:5000/api/products/6");

      setNutDriver(result.data);
    };
    fetchData();
  }, []);
  /*  console.log(
    "This is nutDrivers: ",
    nutDrivers.forEach((obj, i) => {
      console.log("I is: ", i);
      console.log("Data is: ", obj);
      console.log(Object.keys(obj));
    })
  ); */
  const handleSetQty = (qty) => {
    setQty(qty.target.value.split(/\D/).join(""));
  };

  const handleAddToCart = (qty, ProductID, Name, Price) => {
    setQty(qty);
    dispatch(ADD_TO_CART(qty, ProductID, Name, Price));
    console.log(qty, ProductID, Name, Price);
  };

  return (
    <main className="pl-3 pr-3 fitContent  mt-3">
      <div className="row">
        <div className="dropdown col-12 display2 text-center center">
          <button
            className="btn dropdown-toggle"
            type="button"
            id="dropdownMenuButton"
            data-toggle="dropdown"
            aria-haspopup="true"
            aria-expanded="false"
          >
            <h2>Shop</h2>
          </button>
          <div className="dropdown-menu " aria-labelledby="dropdownMenuButton">
            <NavLink
              className="nav-link-bottom boarderBottom dropdown-item"
              to="/Shop/SpiceRubs"
            >
              SPICE RUBS
            </NavLink>
            <NavLink
              className="nav-link-bottom boarderBottom dropdown-item"
              to="/Shop/Cookbooks"
            >
              COOKBOOKS
            </NavLink>
            <NavLink
              className="nav-link-bottom boarderBottom dropdown-item"
              to="/Shop/BakingPlanks"
            >
              BAKING PLANKS
            </NavLink>
            <NavLink
              className="nav-link-bottom boarderBottom dropdown-item"
              to="/Shop/BBQPlanks"
            >
              BBQ PLANKS
            </NavLink>
            <NavLink
              className="nav-link-bottom boarderBottom dropdown-item"
              to="/Shop/NutDriver "
            >
              NUT DRIVER
            </NavLink>
          </div>
        </div>

        <section className="col-4 display size14Font center">
          <ul className="hideUlDot ">
            <img src={Shop_MenuHeader} alt="This is a pic" />
            <NavLink
              className="nav-link-bottom boarderBottom"
              to="/Shop/SpiceRubs"
            >
              SPICE RUBS
            </NavLink>

            <NavLink
              className="nav-link-bottom boarderBottom"
              to="/Shop/Cookbooks"
            >
              COOKBOOKS
            </NavLink>

            <NavLink
              className="nav-link-bottom boarderBottom"
              to="/Shop/BakingPlanks"
            >
              BAKING PLANKS
            </NavLink>
            <NavLink
              className="nav-link-bottom boarderBottom"
              to="/Shop/BBQPlanks"
            >
              BBQ PLANKS
            </NavLink>
            <NavLink
              className="nav-link-bottom boarderBottom"
              to="/Shop/NutDriver"
            >
              NUT DRIVERS
            </NavLink>
          </ul>
        </section>
      </div>
    </main>
  );
};
export default NutDriverPage;
