import { Promise } from "mssql";
import axios from "axios";
const initialState = {};

const orderCartReducers = (state = initialState, action) => {
  let array = [];
  switch (action.type) {
    case "CHECKOUT_UPDATE_PAGE":
      state = action.payload;
      return state;
    default:
      return state;
  }
};

export default orderCartReducers;
