import React from "react";
import { Route, Switch } from "react-router-dom";

import HomePage from "./components/home/HomePage";

import AboutPage from "./components/about/AboutPage";
import ReviewsPage from "./components/reviews/ReviewsPage";
import ContactPage from "./components/contact/ContactPage";
import CartPage from "./components/cart/CartPage";
import CheckoutPage from "./components/cart/CheckoutPage";

import RecipesPage from "./components/recipes/RecipesPage";
import TestimonialsPage from "./components/reviews/TestimonialsPage";
import SpiceRubsPage from "./components/shop/SpiceRubsPage";
import CookBooksPage from "./components/shop/CookBooksPage";
import BakingPlanksPage from "./components/shop/BakingPlanksPage";
import ShopPage from "./components/shop/ShopPage";
import Header from "./components/shared/Header";
import Footer from "./components/shared/Footer";
import StepThreePage from "./components/cart/StepThreePage";
import StepFourPage from "./components/cart/StepFourPage";
import "bootstrap/dist/css/bootstrap.min.css";
import "jquery/dist/jquery.min.js";
import "bootstrap/dist/js/bootstrap.min.js";
import "./App.css";

const App = (props) => {
  return (
    <>
      <Header />
      <Switch>
        <Route exact path="/" component={HomePage} />
        <Route path="/about" component={AboutPage} />
        <Route path="/contact" component={ContactPage} />
        <Route path="/reviews" component={ReviewsPage} />
        <Route exact path="/cart" component={CartPage} />
        <Route path="/cart/checkout" component={CheckoutPage} />
        <Route path="/cart/stepthree" component={StepThreePage} />
        <Route path="/cart/stepfour" component={StepFourPage} />
        <Route path="/recipes" component={RecipesPage} />
        <Route path="/testimonials" component={TestimonialsPage} />
        <Route exact path="/shop" component={ShopPage} />
        <Route path="/shop/spicerubs" component={SpiceRubsPage} />
        <Route path="/shop/cookbooks" component={CookBooksPage} />
        <Route path="/shop/bakingplanks" component={BakingPlanksPage} />
      </Switch>
      <Footer />
    </>
  );
};

export default App;
